import React, { useState, useEffect, useContext } from 'react';
import { Link, useNavigate} from 'react-router-dom';
import axios from 'axios';
import UserContext from '../context/UserContext';

const PaymentProcess = () => {
  const { authUser } = useContext(UserContext);
  const navigate = useNavigate();
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);
  const [editIndex, setEditIndex] = useState(null);
  const [quantity, setQuantity] = useState('');
  const [notes, setNotes] = useState('');

  useEffect(() => {
    if (!authUser || !authUser._id) {
      console.error('User ID is undefined');
      return;
    }

    const fetchCartData = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/cart`);
        if (response.status === 200) {
          setCart(response.data.cart);
          const totalAmount = response.data.cart.reduce((acc, item) => acc + (item.price * item.quantity), 0);
          setTotal(totalAmount);
        } else {
          console.error('Error fetching cart data: status', response.status);
        }
      } catch (error) {
        console.error('Error fetching cart data:', error);
      }
    };

    fetchCartData();

  }, [authUser]);

  const handleEditItem = (index) => {
    const cartItem = cart[index];
    setEditIndex(index);
    setQuantity(cartItem.quantity);
    setNotes(cartItem.notes);
  };

  const handleCancelEdit = () => {
    setEditIndex(null);
    setQuantity('');
    setNotes('');
  };

  const handleDeleteItem = async (index) => {
    try {
      const updatedCart = cart.filter((_, idx) => idx !== index);
      setCart(updatedCart);
      const response = await axios.delete(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/cart/${cart[index]._id}`);
      if (response.status === 200) {
        console.log('Item removed successfully');
      }
    } catch (error) {
      console.error('Error removing item:', error);
    }
  };

  const handleSaveChanges = async () => {
    try {
      const updatedCart = [...cart];
      updatedCart[editIndex].quantity = quantity;
      updatedCart[editIndex].notes = notes;
      setCart(updatedCart);
      const response = await axios.put(`/api/users/${encodeURIComponent(authUser._id)}/cart/${cart[editIndex]._id}`, { cartItem: updatedCart[editIndex] });
      if (response.status === 200) {
        console.log('Item updated successfully');
        setEditIndex(null);
      }
    } catch (error) {
      console.error('Error updating item:', error);
    }
  };

  const handleGoBack = () => {
    navigate('/');
  };

  return (
    <div>
      <h1>My Cart</h1>
      {cart.length > 0 ? (
        cart.map((cartItem, index) => (
          <div key={index} style={{ marginBottom: '10px', padding: '10px', border: '1px solid #ccc', borderRadius: '5px' }}>
            {editIndex === index ? (
              <div>
                <p>Title: {cartItem.storeName}</p> {/* Display store name */}
                <p>Price: ${parseFloat(cartItem.price).toFixed(2)}</p>
                <p>Quantity: <input type="number" min="1" value={quantity} onChange={(e) => setQuantity(e.target.value)} /></p>
                <p>Notes: <input type="text" value={notes} onChange={(e) => setNotes(e.target.value)} /></p>
                <button onClick={handleSaveChanges}>Save Changes</button>
                <button onClick={handleCancelEdit}>Cancel</button>
              </div>
            ) : (
              <div>
                <p>{cartItem.itemName}</p>
                <p>Price: ${parseFloat(cartItem.price).toFixed(2)}</p>
                <p>Quantity: {cartItem.quantity}</p>
                <p>Notes: {cartItem.notes}</p>
                <button onClick={() => handleEditItem(index)}>Edit</button>
                <button onClick={() => handleDeleteItem(index)}>Delete</button>
              </div>
            )}
          </div>
        ))
      ) : (
        <p>Cart is empty</p>
      )}
      <h3>Total: ${total.toFixed(2)}</h3>
      <button onClick={handleGoBack}>Go Back</button>
    </div>
  );
};

export default PaymentProcess;
