import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import UserContext from '../context/UserContext';

const Kitchen = () => {
  const { authUser } = useContext(UserContext);
  const [orders, setOrders] = useState([]);
  const [expandedOrders, setExpandedOrders] = useState({});
  const [checkedItems, setCheckedItems] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/orders`);
        if (response.status === 200) {
          setOrders(response.data.orders);
        } else {
          console.error('Error fetching orders: status', response.status);
        }
      } catch (error) {
        console.error('Error fetching orders:', error);
      }
    };

    if (authUser && authUser._id) {
      fetchOrders();
    }
  }, [authUser]);

  const toggleOrder = (index) => {
    setExpandedOrders(prevState => ({
      ...prevState,
      [index]: !prevState[index]
    }));
  };

  const handleDeleteOrder = async (index) => {
    const confirmDelete = window.confirm('Are you sure you want to delete this order?');
    if (confirmDelete) {
      try {
        const response = await axios.delete(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/orders/${index}`);
        if (response.status === 200) {
          setOrders(orders.filter((_, i) => i !== index));
        } else {
          console.error('Error deleting order:', response.status);
        }
      } catch (error) {
        console.error('Error deleting order:', error);
      }
    }
  };

  const handleCheckItem = (orderIndex, itemIndex) => {
    const updatedCheckedItems = { ...checkedItems };
    if (!updatedCheckedItems[orderIndex]) {
      updatedCheckedItems[orderIndex] = {};
    }
    updatedCheckedItems[orderIndex][itemIndex] = !updatedCheckedItems[orderIndex][itemIndex];
    setCheckedItems(updatedCheckedItems);
  };

  const isOrderComplete = (orderIndex) => {
    const order = orders[orderIndex];
    const orderCheckedItems = checkedItems[orderIndex] || {};
    return order.items.every((_, itemIndex) => orderCheckedItems[itemIndex]);
  };

  const orderCount = orders.length;

  return (
    <div>
      <h1>Kitchen Orders</h1>
      <p>Total Orders: {orderCount}</p>
      {orders.length > 0 ? (
        orders.map((order, index) => (
          <div key={index} style={{ marginBottom: '10px', padding: '10px', border: '1px solid #ccc', borderRadius: '5px', position: 'relative' }}>
            <div onClick={() => toggleOrder(index)} style={{ cursor: 'pointer', borderBottom: '1px solid black', paddingBottom: '10px' }}>
              <h2>Order {index + 1} {isOrderComplete(index) && <span style={{ color: 'red' }}>X</span>}</h2>
              <p>Main Key: {order.mainkey}</p>
              <p>Timestamp: {order.timestamp}</p>
              <p>Items Count: {order.items.length}</p>
            </div>
            {expandedOrders[index] && (
              <div>
                <h3>Items:</h3>
                {order.items.map((item, itemIndex) => (
                  <div key={itemIndex} style={{ marginBottom: '5px' }}>
                    <p style={{ backgroundColor: 'black', color: 'white' }}>Item Name: {item.itemName}</p>
                    <p>Quantity: {item.quantity}</p>
                    <p>Price: {item.price}</p>
                    <label>
                      <input
                        type="checkbox"
                        checked={checkedItems[index] && checkedItems[index][itemIndex]}
                        onChange={() => handleCheckItem(index, itemIndex)}
                      />
                      Mark as completed
                    </label>
                  </div>
                ))}
              </div>
            )}
            <button onClick={() => handleDeleteOrder(index)} style={{ position: 'absolute', top: '10px', right: '10px' }}>Delete Order</button>
          </div>
        ))
      ) : (
        <p>No orders found</p>
      )}
    </div>
  );
};

export default Kitchen;
