import React, { useState, useEffect, useContext } from 'react';
import UserContext from "../context/UserContext";
import axios from 'axios';

const CartComp = ({ cart, total }) => {
  const { authUser } = useContext(UserContext);
  const [localCart, setLocalCart] = useState(cart || []);
  const [localTotal, setLocalTotal] = useState(total || 0);

  useEffect(() => {
    if (!cart) {
      const fetchCartData = async () => {
        try {
          const response = await axios.get(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/cart`);
          if (response.status === 200) {
            const validCartItems = response.data.cart.filter(item => item && item.price !== undefined && item.quantity !== undefined);
            setLocalCart(validCartItems);
            updateLocalTotal(validCartItems);
          } else {
            console.error('Error fetching cart data: status', response.status);
          }
        } catch (error) {
          console.error('Error fetching cart data:', error);
        }
      };
      fetchCartData();
    }
  }, [authUser._id, cart]);

  const updateLocalTotal = (cartItems) => {
    const totalAmount = cartItems.reduce((acc, item) => acc + (item.price * item.quantity), 0);
    setLocalTotal(totalAmount);
  };

  return (
    <div>
      {localCart.map(item => (
        <div key={item._id}>
          <p>{item.itemName} - {item.quantity} x ${item.price}</p>
        </div>
      ))}
      <p>Total: ${localTotal.toFixed(2)}</p>
    </div>
  );
};

export default CartComp;
