import React, { useState, useEffect } from 'react';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css'; // requires a loader
import './Home.css'; // Ensure this file exists

const Home = () => {
  const [videoVisible, setVideoVisible] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setVideoVisible(false);
    }, 25000); // 25 seconds

    return () => clearTimeout(timer); // Clear timeout if component unmounts
  }, []);

  const styles = {
    container: {
      textAlign: 'center',
      padding: '10px',
      backgroundColor: '#f0f2f5',
      minHeight: '100vh',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'flex-start',
      alignItems: 'center',
      paddingTop: '0px',
    },
    heading: {
      fontSize: '2rem',
      color: '#333',
      marginBottom: '5px',
    },
    carouselContainer: {
      width: '70%',
      maxWidth: '70%',
      marginBottom: '60px',
    },
    image: {
      width: '100%',
      height: 'auto',
      borderRadius: '10px',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    },
    video: {
      width: '100%',
      height: 'auto',
      borderRadius: '10px',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
      opacity: videoVisible ? 1 : 0,
      transition: 'opacity 1s ease-out',
    },
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>Welcome</h1>
      <div style={styles.carouselContainer}>
        <Carousel showThumbs={false} autoPlay={true} infiniteLoop={true} interval={5000}>
          <div>
            <img src="/tfp.png" alt="Example" style={styles.image} />
          </div>
          <div>
            <video style={styles.video} controls>
              <source src="/ftp_homescreen.mp4" type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          </div>
        </Carousel>
      </div>
    </div>
  );
};

export default Home;
