import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { UserContext } from '../context/UserContext';

const StoreDetails = () => {
  const { authUser, store } = useContext(UserContext);
  const [cart, setCart] = useState([]);
  const [quantities, setQuantities] = useState({});
  const [notes, setNotes] = useState({});

  useEffect(() => {
    if (!authUser || !authUser._id) {
      console.error('User ID is undefined');
      return;
    }

    const fetchStoreData = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/store`);
        if (response.status === 200) {
          console.log('Store data fetched:', response.data);
          setStore(response.data);
        } else {
          console.error('Error fetching store data: status', response.status);
        }
      } catch (error) {
        console.error('Error fetching store data:', error);
      }
    };

    fetchStoreData();
  }, [authUser]);

  const handleQuantityChange = (itemId, value) => {
    setQuantities(prev => ({ ...prev, [itemId]: value }));
    console.log('Quantities updated:', { ...quantities, [itemId]: value });
  };

  const handleNotesChange = (itemId, value) => {
    setNotes(prev => ({ ...prev, [itemId]: value }));
    console.log('Notes updated:', { ...notes, [itemId]: value });
  };

  const addToCart = async (itemId) => {
    const item = store.items.find(item => item._id === itemId);
    const quantity = quantities[itemId];
    const note = notes[itemId];
    if (!quantity || quantity <= 0) {
      console.error('Quantity must be greater than 0');
      return;
    }

    const cartItem = {
      ...item,
      quantity,
      notes: note,
    };

    try {
      const response = await axios.post(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/cart`, {
        itemId: item._id,
        quantity,
        notes: note,
      });
      if (response.status === 200) {
        setCart(prevCart => [...prevCart, cartItem]);
        console.log('Item added to cart:', cartItem);
      } else {
        console.error('Error adding item to cart: status', response.status);
      }
    } catch (error) {
      console.error('Error adding item to cart:', error);
    }
  };

  console.log('Store items:', store.items);
  console.log('Cart items:', cart);

  return (
    <div>
      <h1 style={{ fontWeight: 'bold', color: 'blue' }}>{store?.name}</h1>
      <p>{store?.description}</p>
      <p>{store?.location}</p>

      <h2>Items</h2>
      {store?.items.length > 0 ? (
        store.items.map(item => (
          <div key={item._id} style={{ marginBottom: '10px', padding: '10px', border: '1px solid #ccc', borderRadius: '5px' }}>
            <h3>{item.title}</h3>
            <p>Price: {item.price}</p>
            <p>Quantity Available: {item.quantity}</p>
            <p>Description: {item.description}</p>
            <div>
              <label>Quantity to Order:</label>
              <input
                type="number"
                min="1"
                max={item.quantity}
                value={quantities[item._id] || ''}
                onChange={(e) => handleQuantityChange(item._id, e.target.value)}
              />
              <label>Notes:</label>
              <input
                type="text"
                value={notes[item._id] || ''}
                onChange={(e) => handleNotesChange(item._id, e.target.value)}
              />
              <button onClick={() => addToCart(item._id)}>Add to Cart</button>
            </div>
          </div>
        ))
      ) : (
        <p>No items available</p>
      )}

      <h2>Cart</h2>
      {cart.length > 0 ? (
        cart.map(cartItem => (
          <div key={cartItem._id}>
            <p>{cartItem.title} - Quantity: {cartItem.quantity} - Notes: {cartItem.notes}</p>
          </div>
        ))
      ) : (
        <p>Cart is empty</p>
      )}
    </div>
  );
};

export default StoreDetails;
