import React, { useState, useContext, useEffect } from 'react';
import axios from 'axios';
import UserContext from '../context/UserContext';
import '../styles/compliment.css';

const Compliment = () => {
  const { authUser } = useContext(UserContext);
  const [title, setTitle] = useState('');
  const [amount, setAmount] = useState('');
  const [startDate, setStartDate] = useState('');
  const [startTime, setStartTime] = useState('');
  const [endTime, setEndTime] = useState('');
  const [quantity, setQuantity] = useState('');
  const [createdCompliments, setCreatedCompliments] = useState([]);
  const [showAll, setShowAll] = useState(false);
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);

  useEffect(() => {
    const fetchCompliments = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/compliments`, {
          headers: {
            Authorization: `Bearer ${authUser.token}`
          }
        });
        setCreatedCompliments(response.data.compliments);
      } catch (error) {
        console.error('Error fetching compliments:', error);
      }
    };
    fetchCompliments();

    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/users', {
          headers: {
            Authorization: `Bearer ${authUser.token}`
          }
        });
        setUsers(response.data.users);
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };
    fetchUsers();
  }, [authUser._id, authUser.token]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/create-compliment`, {
        title,
        amount,
        startDate,
        startTime,
        endTime,
        quantity,
      }, {
        headers: {
          Authorization: `Bearer ${authUser.token}`
        }
      });
      setCreatedCompliments(response.data.compliments);
      // Clear input fields
      setTitle('');
      setAmount('');
      setStartDate('');
      setStartTime('');
      setEndTime('');
      setQuantity('');
    } catch (error) {
      console.error('Error creating compliments:', error);
    }
  };

  const handleSend = async (coupon) => {
    try {
      await axios.post(`http://localhost:5000/api/users/${encodeURIComponent(selectedUser._id)}/send-compliment`, 
      { coupon }, 
      {
        headers: {
          Authorization: `Bearer ${authUser.token}`
        }
      });
      alert('Coupon sent successfully');
    } catch (error) {
      console.error('Error sending coupon:', error);
    }
  };

  const handleDelete = (index) => {
    const newCompliments = [...createdCompliments];
    newCompliments.splice(index, 1);
    setCreatedCompliments(newCompliments);
  };

  const toggleShowAll = () => {
    setShowAll(!showAll);
  };

  return (
    <div className="compliment-container">
      <div className="compliment-card">
        <h2>Create Compliment</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <input
            type="number"
            placeholder="Amount (%)"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
          <input
            type="date"
            placeholder="Start Date"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
          />
          <input
            type="time"
            placeholder="Start Time"
            value={startTime}
            onChange={(e) => setStartTime(e.target.value)}
          />
          <input
            type="time"
            placeholder="End Time"
            value={endTime}
            onChange={(e) => setEndTime(e.target.value)}
          />
          <input
            type="number"
            placeholder="Quantity"
            value={quantity}
            onChange={(e) => setQuantity(e.target.value)}
          />
          <button type="submit">Create Compliment</button>
        </form>
        {createdCompliments.length > 0 && (
          <div className="compliment-result">
            <h1>Compliments Created</h1>
            {createdCompliments.slice(0, 3).map((compliment, index) => (
              <div key={index} className="compliment-item">
                <p><strong>{index + 1}.</strong> Title: {compliment.title}</p>
                <p>Amount: {compliment.amount}%</p>
                <p>Unique Key: {compliment.uniqueKey}</p>
                <p>Group ID: {compliment.groupId}</p>
                <button onClick={() => handleSend(compliment)}>Send</button>
                <button onClick={() => handleDelete(index)}>Delete</button>
                <hr />
              </div>
            ))}
            {showAll && createdCompliments.slice(3).map((compliment, index) => (
              <div key={index + 3} className="compliment-item">
                <p><strong>{index + 4}.</strong> Title: {compliment.title}</p>
                <p>Amount: {compliment.amount}%</p>
                <p>Unique Key: {compliment.uniqueKey}</p>
                <p>Group ID: {compliment.groupId}</p>
                <button onClick={() => handleSend(compliment)}>Send</button>
                <button onClick={() => handleDelete(index + 3)}>Delete</button>
                <hr />
              </div>
            ))}
            {createdCompliments.length > 3 && (
              <button onClick={toggleShowAll} className="toggle-button">
                {showAll ? 'Show Less' : 'Show All'}
              </button>
            )}
          </div>
        )}
      </div>
      <div className="followers-card">
        <h2>Select User</h2>
        <ul>
          {users.map((user) => (
            <li key={user._id} onClick={() => setSelectedUser(user)} className={selectedUser && selectedUser._id === user._id ? 'selected' : ''}>
              {user.username} ({user.email})
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Compliment;
