import { useContext } from 'react';
import { Link } from 'react-router-dom';
import ThemeContext from '../context/ThemeContext';

const Footer = () => {
  const { accentColor } = useContext(ThemeContext);

  const linkStyles = {
    color: accentColor, // Primary color
    textDecoration: 'none', // Remove underline
    fontWeight: 'normal', // Bold text
    fontSize: '1.2rem', // Slightly larger font size
    transition: 'color 0.3s ease, background-color 0.3s ease', // Smooth transition for color and background color
    display: 'inline-block', // Allow padding and margin
    padding: '5px 7.5px', // Padding around the link
    borderRadius: '5px', // Rounded corners
    margin: '10px 0', // Margin to add space around the link
  };

  const linkHoverStyles = {
    color: '#0056b3', // Darker shade on hover
   
  };

  const handleMouseEnter = (e) => {
    Object.assign(e.currentTarget.style, linkHoverStyles);
  };

  const handleMouseLeave = (e) => {
    Object.assign(e.currentTarget.style, linkStyles);
  };

  return (
    <div className="footer" style={{ background: accentColor, padding: '20px', textAlign: 'center' }}>
      <div className="bounds">
        <Link
          to="https://www.instagram.com/theflyinginsta/"
          style={linkStyles}
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
        >
          theflyinginsta
        </Link>
      </div>
    </div>
  );
};

export default Footer;
