import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import UserContext from '../context/UserContext';

const Kitchen = ({ accentColor }) => {
  const { authUser } = useContext(UserContext);
  const [orders, setOrders] = useState([]);
  const [expandedOrders, setExpandedOrders] = useState({});
  const [checkedItems, setCheckedItems] = useState({});

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/orders`);
        if (response.status === 200) {
          setOrders(response.data.orders);
          const initialExpanded = response.data.orders.reduce((acc, _, index) => {
            acc[index] = true; // Set all orders to expanded initially
            return acc;
          }, {});
          setExpandedOrders(initialExpanded);
        } else {
          console.error('Error fetching orders: status', response.status);
        }
      } catch (error) {
        console.error('Error fetching orders:', error);
      }
    };

    if (authUser && authUser._id) {
      fetchOrders();
    }
  }, [authUser]);

  const toggleOrder = (index) => {
    setExpandedOrders(prevState => ({
      ...prevState,
      [index]: !prevState[index]
    }));
  };

  const handleDeleteOrder = async (mainkey) => {
    if (window.confirm('Are you sure you want to delete this order?')) {
      try {
        const response = await axios.delete(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/orders/${encodeURIComponent(mainkey)}`);
        if (response.status === 200) {
          setOrders(orders.filter(order => order.mainkey !== mainkey));
        } else {
          console.error('Error deleting order:', response.status);
        }
      } catch (error) {
        console.error('Error deleting order:', error);
      }
    }
  };

  const handleCheckItem = (orderIndex, itemIndex) => {
    const updatedCheckedItems = { ...checkedItems };
    if (!updatedCheckedItems[orderIndex]) {
      updatedCheckedItems[orderIndex] = {};
    }
    updatedCheckedItems[orderIndex][itemIndex] = !updatedCheckedItems[orderIndex][itemIndex];
    setCheckedItems(updatedCheckedItems);
  };

  const isOrderComplete = (orderIndex) => {
    const orderCheckedItems = checkedItems[orderIndex] || {};
    return orders[orderIndex].items.every((_, itemIndex) => orderCheckedItems[itemIndex]);
  };

  const updateOrderStatus = async (mainkey, status) => {
    try {
      const endpoint = status === 'Ready'
        ? `http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/orders/${encodeURIComponent(mainkey)}/status/ready`
        : `http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/orders/${encodeURIComponent(mainkey)}/status/ready-in-10-minutes`;

      const response = await axios.put(endpoint);
      if (response.status === 200) {
        const updatedOrders = orders.map(order =>
          order.mainkey === mainkey ? { ...order, status } : order
        );
        setOrders(updatedOrders);
      } else {
        console.error('Error updating order status:', response.status);
      }
    } catch (error) {
      console.error('Error updating order status:', error);
    }
  };

  return (
    <div>
      <h1>Kitchen Orders</h1>
      <p>Total Orders: {orders.length}</p>
      {orders.length > 0 ? (
        orders.map((order, index) => (
          <div key={order.mainkey} style={{ marginBottom: '20px', padding: '10px', border: `2px solid ${accentColor}`, borderRadius: '5px', position: 'relative' }}>
            <div onClick={() => toggleOrder(index)} style={{ cursor: 'pointer', borderBottom: `2px solid ${accentColor}`, paddingBottom: '10px' }}>
              <h2 style={{ color: accentColor }}>Order {order.orderNumber} {isOrderComplete(index) && <span style={{ color: 'red' }}>X</span>}</h2>
              <p>Main Key: {order.mainkey}</p>
              <p>Timestamp: {order.timestamp}</p>
              <p>Items Count: {order.items.length}</p>
            </div>
            {expandedOrders[index] && (
              <div>
                <h3>Items:</h3>
                {order.items.map((item, itemIndex) => (
                  <div key={itemIndex} style={{ marginBottom: '10px', borderBottom: `1px solid ${accentColor}`, paddingBottom: '10px' }}>
                    <p style={{ backgroundColor: accentColor, color: 'white', padding: '5px' }}>Item Name: {item.itemName}</p>
                    <p>Item ID: {item.foodId}</p>
                    <p>Quantity: {item.quantity}</p>
                    <p>Notes: {item.notes || 'N/A'}</p>
                    <p>Price: ${item.price}</p>
                    <label>
                      <input
                        type="checkbox"
                        checked={checkedItems[index] && checkedItems[index][itemIndex]}
                        onChange={() => handleCheckItem(index, itemIndex)}
                      />
                      Mark as completed
                    </label>
                  </div>
                ))}
                <button onClick={() => updateOrderStatus(order.mainkey, 'Ready')} style={{ marginRight: '10px', backgroundColor: accentColor, color: 'white', border: 'none', padding: '5px 10px', borderRadius: '3px' }}>Ready</button>
                <button onClick={() => updateOrderStatus(order.mainkey, 'Ready in 10 minutes')} style={{ backgroundColor: accentColor, color: 'white', border: 'none', padding: '5px 10px', borderRadius: '3px' }}>Ready in 10min!</button>
              </div>
            )}
            <button onClick={() => handleDeleteOrder(order.mainkey)} style={{ position: 'absolute', top: '10px', right: '10px', backgroundColor: 'red', color: 'white', border: 'none', padding: '5px 10px', borderRadius: '3px' }}>Delete Order</button>
          </div>
        ))
      ) : (
        <p>No orders found</p>
      )}
    </div>
  );
};

export default Kitchen;
