import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import UserContext from '../context/UserContext';

const CreateStore = () => {
  const [storeName, setStoreName] = useState('');
  const [storeDescription, setStoreDescription] = useState('');
  const [storeLocation, setStoreLocation] = useState('');
  const [items, setItems] = useState([]);
  const [newItem, setNewItem] = useState({ title: '', description: '', price: '', quantity: '' });
  const navigate = useNavigate();
  const { authUser } = useContext(UserContext);

  const handleNewItemChange = (e) => {
    const { name, value } = e.target;
    setNewItem({ ...newItem, [name]: value });
  };

  const addItem = () => {
    // Ensure all fields are filled
    if (newItem.title && newItem.description && newItem.price && newItem.quantity) {
      // Generate a unique _id using Date.now() (you can use other methods for generating unique IDs as well)
      const uniqueId = Date.now().toString();
      const newItemWithId = { ...newItem, _id: uniqueId };
      setItems([...items, newItemWithId]);
      setNewItem({ title: '', description: '', price: '', quantity: '' });
    } else {
      alert('Please fill in all fields before adding an item.');
    }
  };

  const createStore = async () => {
    if (storeName && storeDescription && storeLocation && items.length > 0) {
      try {
        const response = await axios.post(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/store`, {
          name: storeName,
          description: storeDescription,
          location: storeLocation,
          items: items
        });

        if (response.status === 201) {
          navigate('/authenticated'); // Redirect to authenticated page upon successful creation
        }
      } catch (error) {
        console.error('Error creating store:', error);
      }
    } else {
      alert('Please fill in all store details and add at least one item before creating the store.');
    }
  };

  return (
    <div>
      <h2>Create Store</h2>
      <div>
        <label>Store Name:</label>
        <input type="text" value={storeName} onChange={(e) => setStoreName(e.target.value)} required />
      </div>
      <div>
        <label>Store Description:</label>
        <input type="text" value={storeDescription} onChange={(e) => setStoreDescription(e.target.value)} required />
      </div>
      <div>
        <label>Store Location:</label>
        <input type="text" value={storeLocation} onChange={(e) => setStoreLocation(e.target.value)} required />
      </div>
      <div>
        <h3>Add Items</h3>
        <div>
          <label>Title:</label>
          <input type="text" name="title" value={newItem.title} onChange={handleNewItemChange} required />
        </div>
        <div>
          <label>Description:</label>
          <input type="text" name="description" value={newItem.description} onChange={handleNewItemChange} required />
        </div>
        <div>
          <label>Price:</label>
          <input type="number" name="price" value={newItem.price} onChange={handleNewItemChange} required />
        </div>
        <div>
          <label>Quantity:</label>
          <input type="number" name="quantity" value={newItem.quantity} onChange={handleNewItemChange} required />
        </div>
        <button onClick={addItem}>Add Item</button>
      </div>
      <div>
        <h3>Items Added</h3>
        <ul>
          {items.map((item) => (
            <li key={item._id}>{item.title} - {item.description} - ${item.price} - Quantity: {item.quantity}</li>
          ))}
        </ul>
      </div>
      <button onClick={createStore}>Create Store</button>
    </div>
  );
};

export default CreateStore;
