import useTheme from './useTheme';

const useKitchenStyles = () => {
  const { accentColor } = useTheme();

  return {
    defaultStyles: {
      border: `2px solid ${accentColor}`,
      borderRadius: '5px',
      padding: '20px', // Increased padding for more space
      margin: '10px 0',
      fontFamily: 'Arial, sans-serif',
      color: '#333',
    },
    paragraphStyles: {
      margin: '10px 0',
      lineHeight: '1.5',
    },
    itemNameStyles: {
      fontSize: '1.5em',
      fontWeight: 'bold',
      marginBottom: '10px',
    },
    buttonStyles: {
      border: 'none',
      padding: '10px 15px',
      borderRadius: '5px',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '5px',
      fontSize: '1em',
    },
    buttonReadyStyles: {
      backgroundColor: '#28a745', // Green for ready
      color: 'white',
      marginRight: '10px', // Added margin-right for spacing
    },
    buttonReadyIn10Styles: {
      backgroundColor: '#ffc107', // Yellow for ready in 10 minutes
      color: 'black',
    },
    buttonDeleteStyles: {
      backgroundColor: '#dc3545', // Red for delete
      color: 'white',
      position: 'absolute',
      top: '10px',
      right: '10px',
    },
  };
};
