import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import UserContext from '../context/UserContext';

const PaymentProcess = () => {
  const { authUser } = useContext(UserContext);
  const navigate = useNavigate();
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);
  const [editIndex, setEditIndex] = useState(null);
  const [editCartItem, setEditCartItem] = useState({});
  const [editedQuantity, setEditedQuantity] = useState('');
  const [editedNotes, setEditedNotes] = useState('');

  useEffect(() => {
    if (!authUser || !authUser._id) {
      console.error('User ID is undefined');
      return;
    }

    const fetchCartData = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/cart`);
        if (response.status === 200) {
          setCart(response.data.cart);
          const totalAmount = response.data.cart.reduce((acc, item) => acc + (item.price * item.quantity), 0);
          setTotal(totalAmount);
        } else {
          console.error('Error fetching cart data: status', response.status);
        }
      } catch (error) {
        console.error('Error fetching cart data:', error);
      }
    };

    fetchCartData();

  }, [authUser]);

  const handleEditItem = (index) => {
    setEditIndex(index);
    setEditCartItem({ ...cart[index] });
    setEditedQuantity(cart[index].quantity);
    setEditedNotes(cart[index].notes);
  };

  const handleSaveChanges = () => {
    const updatedCart = cart.map((item, index) => {
      if (index === editIndex) {
        return { ...item, quantity: editedQuantity, notes: editedNotes };
      }
      return item;
    });
    setCart(updatedCart);
    setEditIndex(null);
    updateCartOnServer(updatedCart); // Call function to update cart on the server
  };
  

  const handleCancelEdit = () => {
    setEditIndex(null);
    setEditCartItem({});
    setEditedQuantity('');
    setEditedNotes('');
  };

  const handleDeleteItem = (index) => {
    const updatedCart = cart.filter((_, idx) => idx !== index);
    setCart(updatedCart);
    updateCartOnServer(updatedCart); // Call function to update cart on the server
  };

  const updateCartOnServer = async (updatedCart) => {
    try {
      const response = await axios.post(`/api/users/${encodeURIComponent(authUser._id)}/cart`, { cart: updatedCart });
      if (response.status === 200) {
        console.log('Cart updated successfully');
      } else {
        console.error('Messed it up', response.status);
      }
    } catch (error) {
      console.error('second look:', updateCart);
    }
  };

  const handleGoBack = () => {
    navigate('/');
  };

  return (
    <div>
      <h1>My Cart</h1>
      {cart.length > 0 ? (
        cart.map((cartItem, index) => (
          <div key={index} style={{ marginBottom: '10px', padding: '10px', border: '1px solid #ccc', borderRadius: '5px' }}>
            {editIndex === index ? (
              <div>
                <p>Title: {cartItem.storeName}</p> {/* Display store name */}
                <p>Price: ${parseFloat(cartItem.price).toFixed(2)}</p>
                <p>Quantity: <input type="number" min="1" value={editedQuantity} onChange={(e) => setEditedQuantity(e.target.value)} /></p>
                <p>Notes: <input type="text" value={editedNotes} onChange={(e) => setEditedNotes(e.target.value)} /></p>
                <button onClick={handleSaveChanges}>Save Changes</button>
                <button onClick={handleCancelEdit}>Cancel</button>
              </div>
            ) : (
              <div>
                <p>{cartItem.itemName}</p>
                <p>Price: ${parseFloat(cartItem.price).toFixed(2)}</p>
                <p>Quantity: {cartItem.quantity}</p>
                <p>Notes: {cartItem.notes}</p>
                <button onClick={() => handleEditItem(index)}>Edit</button>
                <button onClick={() => handleDeleteItem(index)}>Delete</button>
              </div>
            )}
          </div>
        ))
      ) : (
        <p>Cart is empty</p>
      )}
      <h3>Total: ${total.toFixed(2)}</h3>
      <button onClick={handleGoBack}>Go Back</button>
    </div>
  );
};

export default PaymentProcess;
