import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Import axios for HTTP requests

const PaymentProcess = ({ authUser }) => {
  const [store, setStore] = useState({ items: [] });
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);
  const [editIndex, setEditIndex] = useState(null);
  const [editCartItem, setEditCartItem] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const fetchStoreData = async () => {
      try {
        const response = await axios.get(`/api/users/${encodeURIComponent(authUser._id)}/store`);
        if (response.status === 200) {
          setStore(response.data);
          console.log(response.data)
        } else {
          console.error('Error fetching store data: status', response.status);
        }
      } catch (error) {
        console.error('Error fetching store data:', error);
      }
    };

    const fetchCartData = async () => {
      try {
        const response = await axios.get(`/api/users/${encodeURIComponent(authUser._id)}/cart`);
        if (response.status === 200) {
          setCart(response.data.cart);
          const totalAmount = response.data.cart.reduce((acc, item) => acc + (item.price * item.quantity), 0);
          setTotal(totalAmount);
        } else {
          console.error('Error fetching cart data: status', response.status);
        }
      } catch (error) {
        console.error('Error fetching cart data:', error);
      }
    };

    if (authUser) {
      fetchStoreData();
      fetchCartData();
    }
  }, [authUser]);

  const handleQuantityChange = (index, value) => {
    const updatedCartItem = { ...editCartItem, quantity: value };
    setEditCartItem(updatedCartItem);
  };

  const handleNotesChange = (index, value) => {
    const updatedCartItem = { ...editCartItem, notes: value };
    setEditCartItem(updatedCartItem);
  };

  const handleEditItem = (index) => {
    setEditIndex(index);
    setEditCartItem({ ...cart[index] });
  };

  const handleSaveChanges = () => {
    const updatedCart = [...cart];
    const item = updatedCart[editIndex];
    const priceDifference = item.price * (editCartItem.quantity - item.quantity);
    updatedCart[editIndex] = { ...editCartItem };
    setCart(updatedCart);
    setTotal(prevTotal => prevTotal + priceDifference);
    setEditIndex(null);
    updateCartOnServer(updatedCart); // Call function to update cart on the server
  };

  const handleCancelEdit = () => {
    setEditIndex(null);
    setEditCartItem({});
  };

  const handleDeleteItem = (index) => {
    const updatedCart = [...cart];
    const item = updatedCart.splice(index, 1)[0];
    setCart(updatedCart);
    setTotal(prevTotal => prevTotal - (item.price * item.quantity));
    updateCartOnServer(updatedCart); // Call function to update cart on the server
  };

  const updateCartOnServer = async (updatedCart) => {
    try {
      const response = await axios.post(`/api/users/${encodeURIComponent(authUser._id)}/cart`, { cart: updatedCart });
      if (response.status === 200) {
        console.log('Cart updated successfully');
      } else {
        console.error('Error updating cart: status', response.status);
      }
    } catch (error) {
      console.error('Error updating cart:', error);
    }
  };

  const handleGoBack = () => {
    navigate('/');
  };

  return (
    <div>
      <h1>My Cart</h1>
      {cart.length > 0 ? (
        cart.map((cartItem, index) => (
          <div key={index} style={{ marginBottom: '10px', padding: '10px', border: '1px solid #ccc', borderRadius: '5px' }}>
            {editIndex === index ? (
              <div>
                <p>Title: {store.name}</p> {/* Display store name */}
                <p>Price: ${parseFloat(editCartItem.price).toFixed(2)}</p>
                <p>Quantity: <input type="number" min="1" value={editCartItem.quantity} onChange={(e) => handleQuantityChange(index, parseInt(e.target.value))} /></p>
                <p>Notes: <input type="text" value={editCartItem.notes} onChange={(e) => handleNotesChange(index, e.target.value)} /></p>
                <button onClick={handleSaveChanges}>Save Changes</button>
                <button onClick={handleCancelEdit}>Cancel</button>
              </div>
            ) : (
              <div>
                <p>{cartItem.itemName}</p>
                <p>Price: ${parseFloat(cartItem.price).toFixed(2)}</p>
                <p>Quantity: {cartItem.quantity}</p>
                <p>Notes: {cartItem.notes}</p>
                <button onClick={() => handleEditItem(index)}>Edit</button>
                <button onClick={() => handleDeleteItem(index)}>Delete</button>
              </div>
            )}
          </div>
        ))
      ) : (
        <p>Cart is empty</p>
      )}
      <h3>Total: ${total.toFixed(2)}</h3>
      <button onClick={handleGoBack}>Go Back</button>
    </div>
  );
};

export default PaymentProcess;
