import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import UserContext from '../context/UserContext';
import {
  useDefaultStyles,
  paragraphStyles,
  buttonReadyStyles,
  buttonReadyIn10Styles,
  buttonDeleteStyles,
  itemNameStyles,
} from '../context/Default_Designs';

const Kitchen = ({ orders, setOrders }) => {
  const { authUser } = useContext(UserContext);
  const defaultStyles = useDefaultStyles();
  const [expandedOrders, setExpandedOrders] = useState({});
  const [checkedItems, setCheckedItems] = useState({});
  const [updateCounts, setUpdateCounts] = useState({});

  useEffect(() => {
    const initialExpanded = orders.reduce((acc, _, index) => {
      acc[index] = false; // Set all orders to collapsed initially
      return acc;
    }, {});
    setExpandedOrders(initialExpanded);

    const initialUpdateCounts = orders.reduce((acc, order) => {
      acc[order.mainkey] = { ready: 0, readyIn10: 0 };
      return acc;
    }, {});
    setUpdateCounts(initialUpdateCounts);
  }, [orders]);

  const toggleOrder = (index) => {
    setExpandedOrders((prevState) => ({
      ...prevState,
      [index]: !prevState[index],
    }));
  };

  const handleDeleteOrder = async (mainkey) => {
    if (window.confirm('Are you sure you want to delete this order?')) {
      try {
        const response = await axios.delete(
          `http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/orders/${encodeURIComponent(mainkey)}`
        );
        if (response.status === 200) {
          setOrders(orders.filter((order) => order.mainkey !== mainkey));
        } else {
          console.error('Error deleting order:', response.status);
        }
      } catch (error) {
        console.error('Error deleting order:', error);
      }
    }
  };

  const handleCheckItem = (orderIndex, itemIndex) => {
    const updatedCheckedItems = { ...checkedItems };
    if (!updatedCheckedItems[orderIndex]) {
      updatedCheckedItems[orderIndex] = {};
    }
    const isChecked = updatedCheckedItems[orderIndex][itemIndex];
    if (isChecked && !window.confirm('Are you sure you want to unmark this item as completed?')) {
      return;
    }
    updatedCheckedItems[orderIndex][itemIndex] = !isChecked;
    setCheckedItems(updatedCheckedItems);
  };

  const isOrderComplete = (orderIndex) => {
    const orderCheckedItems = checkedItems[orderIndex] || {};
    return orders[orderIndex].items.every((_, itemIndex) => orderCheckedItems[itemIndex]);
  };

  const updateOrderStatus = async (mainkey, status, PatronId) => {
    try {
      const endpoint =
        status === 'Ready'
          ? `http://localhost:5000/api/patronOrders/${encodeURIComponent(mainkey)}/status/ready`
          : `http://localhost:5000/api/patronOrders/${encodeURIComponent(mainkey)}/status/ready-in-10-minutes`;

      const response = await axios.put(endpoint, { PatronId });
      if (response.status === 200) {
        const updatedOrders = orders.map((order) =>
          order.mainkey === mainkey ? { ...order, status } : order
        );
        setOrders(updatedOrders);
        setUpdateCounts((prevCounts) => ({
          ...prevCounts,
          [mainkey]: {
            ...prevCounts[mainkey],
            [status === 'Ready' ? 'ready' : 'readyIn10']: prevCounts[mainkey][status === 'Ready' ? 'ready' : 'readyIn10'] + 1,
          },
        }));
      } else {
        console.error('Error updating order status:', response.status);
      }
    } catch (error) {
      console.error('Error updating order status:', error);
    }
  };

  const getItemCount = (items) => items.reduce((sum, item) => sum + item.quantity, 0);

  return (
    <div>
      <h2>Kitchen Orders</h2>
      <div style={{ ...defaultStyles, color: 'black' }}>
        <p style={paragraphStyles}>Total Orders: {orders.length}</p>
      </div>
      {orders.length > 0 ? (
        orders.map((order, index) => (
          <div key={order.mainkey} style={{ ...defaultStyles, position: 'relative' }}>
            <div
              onClick={() => toggleOrder(index)}
              style={{
                cursor: 'pointer',
                borderBottom: '2px solid black',
                paddingBottom: '10px',
                marginBottom: '10px',
              }}
            >
              <h2 style={{ color: defaultStyles.backgroundColor }}>
                Order {order.orderNumber} {isOrderComplete(index) && <span style={{ color: 'red' }}>X</span>}
              </h2>
              <p>Main Key: {order.mainkey}</p>
              <p>Timestamp: {order.timestamp}</p>
              <p>Items Count: {getItemCount(order.items)}</p>
            </div>
            {expandedOrders[index] && (
              <div style={{ paddingLeft: '20px' }}> {/* Added padding for more space */}
                <h3>Items:</h3>
                {order.items.map((item, itemIndex) => (
                  <div
                    key={itemIndex}
                    style={{
                      marginBottom: '10px',
                      borderBottom: `1px solid ${defaultStyles.backgroundColor}`,
                      paddingBottom: '10px',
                    }}
                  >
                    <p style={itemNameStyles}>Item Name: {item.itemName}</p>
                    <p>Item ID: {item.foodId}</p>
                    <p>Quantity: {item.quantity}</p>
                    <p>Notes: {item.notes || 'N/A'}</p>
                    <p>Price: ${item.price}</p>
                    <label>
                      <input
                        type="checkbox"
                        checked={checkedItems[index] && checkedItems[index][itemIndex]}
                        onChange={() => handleCheckItem(index, itemIndex)}
                      />
                      {checkedItems[index] && checkedItems[index][itemIndex] ? 'Unmark' : 'Mark as completed'}
                    </label>
                  </div>
                ))}
                <div style={{ display: 'flex', gap: '10px' }}>
                  <button
                    onClick={() => updateOrderStatus(order.mainkey, 'Ready', order.PatronId)}
                    style={buttonReadyStyles}
                  >
                    Ready {updateCounts[order.mainkey]?.ready || 0}
                  </button>
                  <button
                    onClick={() => updateOrderStatus(order.mainkey, 'Ready in 10 minutes', order.PatronId)}
                    style={buttonReadyIn10Styles}
                  >
                    Ready in 10min! {updateCounts[order.mainkey]?.readyIn10 || 0}
                  </button>
                </div>
              </div>
            )}
            <button
              onClick={() => handleDeleteOrder(order.mainkey)}
              style={buttonDeleteStyles}
            >
              Delete Order
            </button>
          </div>
        ))
      ) : (
        <p>No orders found</p>
      )}
    </div>
  );
};

export default Kitchen;
