import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import UserContext from '../context/UserContext'; // Import UserContext to access user information

const CreateStore = () => {
  const [storeName, setStoreName] = useState('');
  const [items, setItems] = useState([{ title: '', price: '', quantity: '', description: '' }]);
  const navigate = useNavigate();
  const { authUser } = useContext(UserContext); // Access authUser from UserContext

  const handleStoreNameChange = (e) => {
    setStoreName(e.target.value);
  };

  const handleItemChange = (index, e) => {
    const updatedItems = items.map((item, i) => {
      if (i === index) {
        return { ...item, [e.target.name]: e.target.value };
      }
      return item;
    });
    setItems(updatedItems);
  };

  const addItem = () => {
    setItems([...items, { title: '', price: '', quantity: '', description: '' }]);
  };

  const removeItem = (index) => {
    setItems(items.filter((_, i) => i !== index));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const userId = authUser.id; // Access user ID from authUser

    try {
      const response = await axios.post(`/api/users/${userId}/store`, {
        name: storeName,
        items: items,
      });

      if (response.status === 201) {
        // Navigate to some success page or reset the form
        navigate('/success'); // Replace with the desired route
      }
    } catch (err) {
      console.error('Error creating store:', err);
      // Handle error (e.g., show an error message)
    }
  };

  return (
    <div>
      <h1>Create Store</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Store Name:</label>
          <input type="text" value={storeName} onChange={handleStoreNameChange} required />
        </div>
        <h2>Items</h2>
        {items.map((item, index) => (
          <div key={index}>
            <div>
              <label>Title:</label>
              <input type="text" name="title" value={item.title} onChange={(e) => handleItemChange(index, e)} required />
            </div>
            <div>
              <label>Price:</label>
              <input type="number" name="price" value={item.price} onChange={(e) => handleItemChange(index, e)} required />
            </div>
            <div>
              <label>Quantity:</label>
              <input type="number" name="quantity" value={item.quantity} onChange={(e) => handleItemChange(index, e)} required />
            </div>
            <div>
              <label>Description:</label>
              <input type="text" name="description" value={item.description} onChange={(e) => handleItemChange(index, e)} required />
            </div>
            <button type="button" onClick={() => removeItem(index)}>Remove Item</button>
          </div>
        ))}
        <button type="button" onClick={addItem}>Add Item</button>
        <button type="submit">Create Store</button>
      </form>
    </div>
  );
};

export default CreateStore;
