import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import UserContext from '../context/UserContext';
import ThemeContext from '../context/ThemeContext';
import { Link } from 'react-router-dom';
import Cart from './CartComp';

import {
  defaultStyles,
  divStyles
} from '../context/Default_Designs';

const StoreDetails = () => {
  const { authUser } = useContext(UserContext);
  const [store, setStore] = useState({ items: [] });
  const [cart, setCart] = useState([]);
  const [quantities, setQuantities] = useState({});
  const [notes, setNotes] = useState({});
  const [total, setTotal] = useState(0);

  useEffect(() => {
    const fetchStoreData = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/stores/${encodeURIComponent(authUser._id)}`);
        if (response.status === 200) {
          setStore(response.data);
        } else {
          console.error('Error fetching store data: status', response.status);
        }
      } catch (error) {
        console.error('Error fetching store data:', error);
      }
    };

    const fetchCartData = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/cart`);
        if (response.status === 200) {
          const validCartItems = response.data.cart.filter(item => item && item.price !== undefined && item.quantity !== undefined);
          setCart(validCartItems);
          updateTotal(validCartItems);
        } else {
          console.error('Error fetching cart data: status', response.status);
        }
      } catch (error) {
        console.error('Error fetching cart data:', error);
      }
    };

    if (authUser && authUser._id) {
      fetchStoreData();
      fetchCartData();
    }
  }, [authUser]);

  const updateTotal = (cartItems) => {
    const totalAmount = cartItems.reduce((acc, item) => acc + (item.price * item.quantity), 0);
    setTotal(totalAmount);
  };

  const handleQuantityChange = (itemId, value) => {
    setQuantities(prev => ({ ...prev, [itemId]: value }));
  };

  const handleNotesChange = (itemId, value) => {
    setNotes(prev => ({ ...prev, [itemId]: value }));
  };

  const addItemToCart = async (item) => {
    const userId = authUser._id;
    const storeId = store.storeId;
    const itemWithStoreIdAndFoodId = {
      ...item,
      storeId,
      foodId: item._id
    };

    try {
      const response = await axios.post(`http://localhost:5000/api/users/${encodeURIComponent(userId)}/cart`, { item: itemWithStoreIdAndFoodId });
      if (response.status === 200) {
        const cartResponse = await axios.get(`http://localhost:5000/api/users/${encodeURIComponent(userId)}/cart`);
        setCart(cartResponse.data.cart);
        updateTotal(cartResponse.data.cart);
      }
    } catch (error) {
      console.error('Error adding item to cart:', error);
    }
  };

  const addToCart = async (itemId) => {
    const item = store.items.find(item => item._id === itemId);
    const quantity = quantities[itemId];
    const note = notes[itemId];
    if (!quantity || quantity <= 0) {
      console.error('Quantity must be greater than 0');
      return;
    }

    const cartItem = {
      _id: '_' + Math.random().toString(36).substr(2, 9),
      storeName: store.name,
      itemName: item.title,
      quantity: quantity,
      notes: note,
      price: item.price,
      foodId: item._id
    };

    try {
      await addItemToCart(cartItem);
      setQuantities(prev => ({ ...prev, [itemId]: '' }));
      setNotes(prev => ({ ...prev, [itemId]: '' }));
    } catch (error) {
      console.error('Error adding item to cart:', error);
    }
  };

  return (
    <div style={divStyles}>
      <h1 style={{ fontWeight: 'bold' }}>{store.name}</h1>
      <p>{store.description}</p>
      <p>{store.location}</p>

      <h2>Items</h2>
      {store.items.length > 0 ? (
        store.items.map(item => (
          <div key={item._id} style={{ ...defaultStyles, marginBottom: '10px' }}>
            <h3>{item.title}</h3>
            <p>Price: {item.price}</p>
            <p>Quantity Available: {item.quantity}</p>
            <p>Description: {item.description}</p>
            <div>
              <label>Quantity to Order:</label>
              <input
                type="number"
                min="1"
                max={item.quantity}
                value={quantities[item._id] || ''}
                onChange={(e) => handleQuantityChange(item._id, e.target.value)}
              />
              <label>Notes:</label>
              <input
                type="text"
                value={notes[item._id] || ''}
                onChange={(e) => handleNotesChange(item._id, e.target.value)}
              />
              <button onClick={() => addToCart(item._id)}>Add to Cart</button>
            </div>
          </div>
        ))
      ) : (
        <p>No items available</p>
      )}
      <Cart cart={cart.filter(item => item && item.itemName)} total={total} />
      <Link className="paymentprocess" to="/paymentprocess">Pay</Link>
    </div>
  );
};

export default StoreDetails;
