import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';

const BigCartView = () => {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);
  const history = useHistory();

  useEffect(() => {
    const savedCart = JSON.parse(localStorage.getItem('cart'));
    const savedTotal = JSON.parse(localStorage.getItem('total'));
    if (savedCart) setCart(savedCart);
    if (savedTotal) setTotal(savedTotal);
  }, []);

  const handleQuantityChange = (index, value) => {
    const updatedCart = [...cart];
    const item = updatedCart[index];
    const priceDifference = item.price * (value - item.quantity);
    item.quantity = value;
    setCart(updatedCart);
    setTotal(prevTotal => prevTotal + priceDifference);
  };

  const handleNotesChange = (index, value) => {
    const updatedCart = [...cart];
    updatedCart[index].notes = value;
    setCart(updatedCart);
  };

  const handleDeleteItem = (index) => {
    const updatedCart = [...cart];
    const item = updatedCart.splice(index, 1)[0];
    setCart(updatedCart);
    setTotal(prevTotal => prevTotal - (item.price * item.quantity));
  };

  const handleUpdateCart = () => {
    // Save updated cart data back to local storage or send it to the server
    localStorage.setItem('cart', JSON.stringify(cart));
    localStorage.setItem('total', JSON.stringify(total));
    alert('Cart updated successfully');
  };

  const handleGoBack = () => {
    history.push('/');
  };

  return (
    <div>
      <h1>My Cart</h1>
      {cart.length > 0 ? (
        cart.map((cartItem, index) => (
          <div key={index}>
            <p>{cartItem.itemName} - Price: ${parseFloat(cartItem.price).toFixed(2)}</p>
            <label>Quantity:</label>
            <input
              type="number"
              min="1"
              value={cartItem.quantity}
              onChange={(e) => handleQuantityChange(index, parseInt(e.target.value))}
            />
            <label>Notes:</label>
            <input
              type="text"
              value={cartItem.notes}
              onChange={(e) => handleNotesChange(index, e.target.value)}
            />
            <button onClick={() => handleDeleteItem(index)}>Delete</button>
          </div>
        ))
      ) : (
        <p>Cart is empty</p>
      )}
      <h3>Total: ${total.toFixed(2)}</h3>
      <button onClick={handleUpdateCart}>Update Cart</button>
      <button onClick={handleGoBack}>Go Back</button>
    </div>
  );
};

export default BigCartView;
