import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import UserContext from '../context/UserContext';

const UpdateStore = () => {
  const [storeName, setStoreName] = useState('');
  const [storeDescription, setStoreDescription] = useState('');
  const [storeLocation, setStoreLocation] = useState('');
  const [items, setItems] = useState([]);
  const [storeExists, setStoreExists] = useState(false);
  const { authUser } = useContext(UserContext);

  const navigate = useNavigate();

  useEffect(() => {
    const fetchStoreData = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/store`);
        if (response.status === 200) {
          setStoreExists(true);
          const store = response.data;
          setStoreName(store.name);
          setStoreDescription(store.description);
          setStoreLocation(store.location);
          setItems(store.items || []);
        } else {
          console.error('Error fetching store data: status', response.status);
        }
      } catch (error) {
        console.error('Error fetching store data:', error);
      }
    };

    if (authUser && authUser._id) {
      fetchStoreData();
    }
  }, [authUser]);

  const deleteStore = async () => {
    try {
      const response = await axios.delete(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/store`);
      if (response.status === 200) {
        console.log('Store deleted successfully');
        setStoreExists(false);
        navigate('/authenticated');
      }
    } catch (error) {
      console.error('Error deleting store:', error);
    }
  };

  const updateItem = async (itemId, updatedItem) => {
    try {
      const response = await axios.put(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/store/items/${encodeURIComponent(itemId)}`, updatedItem);
      if (response.status === 200) {
        console.log('Item updated successfully');
        // Fetch updated store data
        const storeResponse = await axios.get(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/store`);
        if (storeResponse.status === 200) {
          const store = storeResponse.data;
          setStoreName(store.name);
          setStoreDescription(store.description);
          setStoreLocation(store.location);
          setItems(store.items || []);
        }
      }
    } catch (error) {
      console.error('Error updating item:', error);
    }
  };

  return (
    <div>
      {storeExists ? (
        <>
          <h1 style={{ fontWeight: '900' }}>{storeName}</h1>
          <p>{storeDescription}</p>
          <p>{storeLocation}</p>
          <button onClick={deleteStore}>Delete Store</button>
          <Link to="/editstore">Edit Store</Link>
        </>
      ) : (
        <h1>No Store Found</h1>
      )}
      <div>
        <h2>Items</h2>
        {items.map((item, index) => (
          <div key={item._id} style={{ marginBottom: '10px', padding: '10px', border: '1px solid #ccc', borderRadius: '5px' }}>
            <h1>{item.title}</h1>
            <p>Quantity: {item.quantity}</p>
            <p>Description: {item.description}</p>
            <p>Price: ${item.price}</p>
            <button onClick={() => updateItem(item._id, { ...item, quantity: item.quantity - 1 })}>Decrease Quantity</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UpdateStore;
