import React, { useContext } from "react";
import axios from "axios";
import UserContext from "../context/UserContext";
import DarkMode from "./themes/DarkMode";
import AccentColor from "./themes/AccentColor";
import FontSize from "./themes/FontSize";

function Settings() {
  const { authUser } = useContext(UserContext);

  const handleDeleteUser = async () => {
    if (window.confirm('Are you sure you want to delete your account?')) {
      try {
        const response = await axios.delete(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}`);
        if (response.status === 200) {
          alert('Account deleted successfully.');
          // Add any additional logout or redirect logic here
        } else {
          console.error('Error deleting account: status', response.status);
        }
      } catch (error) {
        console.error('Error deleting account:', error);
      }
    }
  };

  return (
    <div className="bounds">
      <div className="grid-100">
        <h1>Preferences</h1>
        <DarkMode />
        <AccentColor />
        <FontSize />
        <button onClick={handleDeleteUser} style={{ backgroundColor: 'red', color: 'white', padding: '10px', borderRadius: '5px', marginTop: '20px' }}>
          Delete Account
        </button>
      </div>
    </div>
  );
}

export default Settings;
