import { createContext, useState, useEffect } from "react";
import Cookies from "js-cookie";
import { api } from "../utils/apiHelper";

const UserContext = createContext(null);

export const UserProvider = (props) => {
  const cookie = Cookies.get("authenticatedUser");
  const [authUser, setAuthUser] = useState(cookie ? JSON.parse(cookie) : null);

  useEffect(() => {
    console.log('authUser in UserProvider:', authUser);
  }, [authUser]);

  const signIn = async (credentials) => {
    try {
      const response = await api("/signin", "POST", credentials);
      if (response.status === 200) {
        const data = await response.json();
        setAuthUser(data.user);
        Cookies.set("authenticatedUser", JSON.stringify(data.user), { expires: 1 });
        return data.user;
      } else if (response.status === 401) {
        console.error('Sign-in failed: Invalid email or password');
        return null;
      } else if (response.status === 403) {
        console.error('Sign-in failed: Email not verified');
        return null;
      } else {
        console.error('Sign-in failed: Unknown error');
        return null;
      }
    } catch (error) {
      console.error('Error during sign-in:', error);
      throw new Error("Sign-in failed");
    }
  };

  const signOut = () => {
    setAuthUser(null);
    Cookies.remove("authenticatedUser");
  };

  return (
    <UserContext.Provider value={{
      authUser,
      actions: {
        signIn,
        signOut
      }
    }}>
      {props.children}
    </UserContext.Provider>
  );
};

export default UserContext;
