import React, { useState, useContext, useEffect } from 'react';
import axios from 'axios';
import UserContext from '../context/UserContext';
import useTheme from '../context/useTheme';
import '../styles/compliment.css';

const Compliment = () => {
  const { authUser } = useContext(UserContext);
  const { accentColor } = useTheme();
  const [title, setTitle] = useState('');
  const [amount, setAmount] = useState('');
  const [startDate, setStartDate] = useState('');
  const [startTime, setStartTime] = useState('');
  const [endTime, setEndTime] = useState('');
  const [quantity, setQuantity] = useState('');
  const [createdCompliments, setCreatedCompliments] = useState([]);
  const [followers, setFollowers] = useState([]);
  const [selectedCompliments, setSelectedCompliments] = useState([]);
  const [selectedFollowers, setSelectedFollowers] = useState([]);
  const [showAll, setShowAll] = useState(false);
  const [inputVisible, setInputVisible] = useState(true);

  useEffect(() => {
    const fetchCompliments = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/compliments`);
        setCreatedCompliments(response.data.compliments);
        if (response.data.compliments.length > 0) {
          setInputVisible(false);
        }
      } catch (error) {
        console.error('Error fetching compliments:', error);
      }
    };

    const fetchFollowers = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/users/all`);
        setFollowers(response.data.users);
      } catch (error) {
        console.error('Error fetching followers:', error);
      }
    };

    fetchCompliments();
    fetchFollowers();
  }, [authUser._id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/create-compliment`, {
        title,
        amount,
        startDate,
        startTime,
        endTime,
        quantity,
      });
      setCreatedCompliments(response.data.compliments);
      // Hide input section after successful creation
      setInputVisible(false);
    } catch (error) {
      console.error('Error creating compliments:', error);
    }
  };

  const handleComplimentSelection = (compliment) => {
    setSelectedCompliments((prevSelected) =>
      prevSelected.includes(compliment)
        ? prevSelected.filter((c) => c !== compliment)
        : [...prevSelected, compliment]
    );
  };

  const handleFollowerSelection = (follower) => {
    setSelectedFollowers((prevSelected) =>
      prevSelected.includes(follower)
        ? prevSelected.filter((f) => f !== follower)
        : [...prevSelected, follower]
    );
  };

  const handleSendCompliments = async () => {
    try {
      const response = await axios.post(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/send-compliments`, {
        compliments: selectedCompliments,
        followers: selectedFollowers,
      });
      console.log('Compliments sent successfully:', response.data);
      // Update the compliments state to mark sent compliments
      const updatedCompliments = createdCompliments.map(compliment =>
        selectedCompliments.includes(compliment)
          ? { ...compliment, sent: true, recipient: selectedFollowers.map(f => f.email).join(', ') }
          : compliment
      );
      setCreatedCompliments(updatedCompliments);
      setSelectedCompliments([]);
      setSelectedFollowers([]);
      alert('Compliments sent successfully');
    } catch (error) {
      console.error('Error sending compliments:', error);
      alert('An error occurred while sending compliments');
    }
  };

  const handleSendComplimentToFollower = async (follower) => {
    const remainingCompliments = createdCompliments.filter(compliment => !compliment.sent);
    if (remainingCompliments.length > 0) {
      const complimentToSend = remainingCompliments[0];
      try {
        await axios.post(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/send-compliments`, {
          compliments: [complimentToSend],
          followers: [follower],
        });
        const updatedCompliments = createdCompliments.map(compliment =>
          compliment.id === complimentToSend.id
            ? { ...compliment, sent: true, recipient: follower.email }
            : compliment
        );
        setCreatedCompliments(updatedCompliments);
      } catch (error) {
        console.error('Error sending compliment:', error);
      }
    }
  };

  const toggleShowAll = () => {
    setShowAll(!showAll);
  };

  const unsentComplimentsCount = createdCompliments.filter(compliment => !compliment.sent).length;

  const isFollowerRecipient = (followerEmail) => {
    return createdCompliments.some(compliment => compliment.recipient === followerEmail && compliment.sent);
  };

  return (
    <div className="compliment-container">
      <div className="compliment-card">
        <h2>Create Compliment</h2>
        {inputVisible && (
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              placeholder="Title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
            <input
              type="number"
              placeholder="Amount (%)"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
            />
            <input
              type="date"
              placeholder="Start Date"
              value={startDate}
              onChange={(e) => setStartDate(e.target.value)}
            />
            <input
              type="time"
              placeholder="Start Time"
              value={startTime}
              onChange={(e) => setStartTime(e.target.value)}
            />
            <input
              type="time"
              placeholder="End Time"
              value={endTime}
              onChange={(e) => setEndTime(e.target.value)}
            />
            <input
              type="number"
              placeholder="Quantity"
              value={quantity}
              onChange={(e) => setQuantity(e.target.value)}
            />
            <button type="submit">Create Compliment</button>
          </form>
        )}
        <hr />
        <h3>Unsent Compliments: {unsentComplimentsCount}</h3>
        {createdCompliments.length > 0 && (
          <div className="compliment-result">
            <h1>{createdCompliments[0].title}</h1>
            {createdCompliments.slice(0, 3).map((compliment, index) => (
              <div
                key={index}
                className={`compliment-item ${selectedCompliments.includes(compliment) ? 'highlight' : ''}`}
                onClick={() => handleComplimentSelection(compliment)}
              >
                <p><strong>ID:</strong> {compliment.id}</p>
                <p><strong>Amount:</strong> {compliment.amount}%</p>
                <p><strong>Start Time:</strong> {compliment.startTime}</p>
                <p><strong>End Time:</strong> {compliment.endTime}</p>
                {compliment.sent && (
                  <>
                    <p><strong>Recipient:</strong> {compliment.recipient}</p>
                    <p><strong>Recipient's Name:</strong> {followers.find(f => f.email === compliment.recipient)?.username}</p>
                    <span className="red-mark">✔</span>
                  </>
                )}
                <hr />
              </div>
            ))}
            {showAll && createdCompliments.slice(3).map((compliment, index) => (
              <div
                key={index + 3}
                className={`compliment-item ${selectedCompliments.includes(compliment) ? 'highlight' : ''}`}
                onClick={() => handleComplimentSelection(compliment)}
              >
                <p><strong>ID:</strong> {compliment.id}</p>
                <p><strong>Amount:</strong> {compliment.amount}%</p>
                <p><strong>Start Time:</strong> {compliment.startTime}</p>
                <p><strong>End Time:</strong> {compliment.endTime}</p>
                {compliment.sent && (
                  <>
                    <p><strong>Recipient:</strong> {compliment.recipient}</p>
                    <p><strong>Recipient's Name:</strong> {followers.find(f => f.email === compliment.recipient)?.username}</p>
                    <span className="red-mark">✔</span>
                  </>
                )}
                <hr />
              </div>
            ))}
            {createdCompliments.length > 3 && (
              <button onClick={toggleShowAll} className="toggle-button">
                {showAll ? 'Show Less' : 'Show All'}
              </button>
            )}
          </div>
        )}
        {followers.length > 0 && (
          <div style={{ border: `1px solid ${accentColor}`}} className="followers-container">
            <h2>Followers</h2>
            <ul>
              {followers.map((follower, index) => (
                <li
                  key={index}
                  className={`follower-item ${selectedFollowers.includes(follower) ? 'highlight' : ''}`}
                  onClick={() => handleFollowerSelection(follower)}
                >
                  <p>Name: {follower.name}</p>
                  <p>Username: {follower.username}</p>
                  <p>Email: {follower.email}</p>
                  {isFollowerRecipient(follower.email) && <span className="red-mark">✔</span>}
                  {!isFollowerRecipient(follower.email) && (
                    <button onClick={() => handleSendComplimentToFollower(follower)}>Send Compliment</button>
                  )}
                </li>
              ))}
            </ul>
          </div>
        )}
        {selectedCompliments.length > 0 && selectedFollowers.length > 0 && (
          <button onClick={handleSendCompliments} className="send-button">
            Send Selected Compliments to Selected Followers
          </button>
        )}
      </div>
    </div>
  );
};

export default Compliment;
