// src/context/default_designs.js
import useTheme from './useTheme';

export const useDefaultStyles = () => {
  const { accentColor } = useTheme();

  return {
    border: `2px solid ${accentColor}`,
    borderRadius: '5px',
    padding: '10px',
    margin: '10px 0',
    fontFamily: 'Arial, sans-serif',
    color: '#333'
  };
};

export const paragraphStyles = {
  margin: '10px 0',
  lineHeight: '1.5',
};

export const divStyles = {
  margin: '10px 0',
  padding: '20px 0'
};

export const buttonStyles = {
  border: 'none',
  padding: '10px 15px',
  borderRadius: '3px',
  cursor: 'pointer',
};

export const buttonReadyStyles = {
  ...buttonStyles,
  backgroundColor: 'grey',
  color: 'white',
};

export const buttonDeleteStyles = {
  ...buttonStyles,
  backgroundColor: 'red',
  color: 'white',
  position: 'absolute',
  top: '10px',
  right: '10px',
};

export const marginStyles = {
  small: {
    margin: '5px',
  },
  medium: {
    margin: '10px',
  },
  large: {
    margin: '15px',
  },
};

export const storeDetailsStyles = {
  container: {
    margin: '20px auto',
    padding: '20px',
    maxWidth: '800px',
    borderRadius: '8px',
    border: `1px solid #ccc`,
    backgroundColor: '#f9f9f9'
  },
  itemContainer: {
    border: `1px solid #ddd`,
    borderRadius: '5px',
    padding: '15px',
    marginBottom: '15px'
  },
  itemTitle: {
    fontWeight: 'bold',
    fontSize: '1.2em',
    marginBottom: '10px'
  },
  itemDescription: {
    marginBottom: '10px'
  },
  itemDetails: {
    display: 'flex',
    justifyContent: 'space-between',
    marginBottom: '10px'
  },
  itemControls: {
    display: 'flex',
    flexDirection: 'column',
    gap: '10px'
  },
  payButton: {
    ...buttonStyles,
    backgroundColor: 'blue',
    color: 'white',
    display: 'inline-block',
    marginTop: '20px',
    textAlign: 'center'
  }
};

export const storesListStyles = {
  
  container: {
    margin: '20px auto',
    padding: '20px',
    maxWidth: '1200px',
    borderRadius: '8px',
    border: `1px solid ${accentColor}`,
    backgroundColor: '#f9f9f9'
  },
  storeContainer: {
    border: `2px solid #000`,
    borderRadius: '5px',
    padding: '15px',
    marginBottom: '20px',
    backgroundColor: '#fff'
  },
  storeTitle: {
    color: '#333',
    cursor: 'pointer',
    marginBottom: '10px'
  },
  storeDetails: {
    fontSize: '0.8em',
    color: '#666'
  }
};
