import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import UserContext from '../context/UserContext';

const CreateStore = () => {
  const [storeName, setStoreName] = useState('');
  const [items, setItems] = useState([{ title: '', price: '', quantity: '', description: '' }]);
  const [storeExists, setStoreExists] = useState(false);
  const { authUser } = useContext(UserContext);
  const [highlightIndex, setHighlightIndex] = useState(null);

  useEffect(() => {
    const fetchStoreData = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/store`);
        if (response.status === 200) {
          setStoreExists(true);
          setStoreName(response.data.name);
        }
      } catch (error) {
        console.error('Error fetching store data:', error);
      }
    };

    fetchStoreData();
  }, [authUser._id]);

  const handleStoreNameChange = (e) => {
    setStoreName(e.target.value);
  };

  const handleItemChange = (index, e) => {
    const updatedItems = [...items];
    updatedItems[index][e.target.name] = e.target.value;
    setItems(updatedItems);
  };

  const addItem = () => {
    setItems([...items, { title: '', price: '', quantity: '', description: '' }]);
    setHighlightIndex(items.length); // Set the highlight index to the new item
    setTimeout(() => setHighlightIndex(null), 2000); // Remove highlight after 2 seconds
  };

  const clearItemFields = () => {
    setItems([{ title: '', price: '', quantity: '', description: '' }]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const userId = authUser._id;
    const baseURL = 'http://localhost:5000/api/users/';
    const requestURL = `${baseURL}${encodeURIComponent(userId)}/store`;

    if (!storeExists) {
      const store = { name: storeName, items: items };

      try {
        const response = await axios.post(requestURL, store);
        if (response.status === 201) {
          console.log('Store added successfully:', response.data);
          setStoreExists(true);
          clearItemFields();
        }
      } catch (error) {
        console.error('Error adding store:', error);
      }
    } else {
      const newItem = items[items.length - 1];

      try {
        const response = await axios.post(`${requestURL}/items`, newItem);
        if (response.status === 200) {
          console.log('Item added successfully:', response.data);
          clearItemFields();
        }
      } catch (error) {
        console.error('Error adding item:', error);
      }
    }
  };

  return (
    <div>
      {storeExists ? (
        <h1>{storeName}</h1>
      ) : (
        <h1>Create Store</h1>
      )}
      <form onSubmit={handleSubmit}>
        {!storeExists && (
          <div>
            <label>Store Name:</label>
            <input type="text" value={storeName} onChange={handleStoreNameChange} required />
          </div>
        )}
        <h2>Items</h2>
        {items.map((item, index) => (
          <div key={index} style={highlightIndex === index ? { backgroundColor: 'yellow', transition: 'background-color 2s ease' } : {}}>
            <div>
              <label>Title:</label>
              <input type="text" name="title" value={item.title} onChange={(e) => handleItemChange(index, e)} required />
            </div>
            <div>
              <label>Price:</label>
              <input type="number" name="price" value={item.price} onChange={(e) => handleItemChange(index, e)} required />
            </div>
            <div>
              <label>Quantity:</label>
              <input type="number" name="quantity" value={item.quantity} onChange={(e) => handleItemChange(index, e)} required />
            </div>
            <div>
              <label>Description:</label>
              <input type="text" name="description" value={item.description} onChange={(e) => handleItemChange(index, e)} required />
            </div>
          </div>
        ))}
        <button type="button" onClick={addItem}>New Field</button>
        <button type="submit">{storeExists ? 'Add Item' : 'Create Store'}</button>
      </form>
    </div>
  );
};

export default CreateStore;
