import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import UserContext from '../context/UserContext';

const CreateStore = () => {
  const [storeName, setStoreName] = useState('');
  const [storeDescription, setStoreDescription] = useState('');
  const [storeLocation, setStoreLocation] = useState('');
  const [items, setItems] = useState([]);
  const [storeExists, setStoreExists] = useState(false);
  const { authUser } = useContext(UserContext);
  const [newItemData, setNewItemData] = useState({ title: '', description: '', price: '', quantity: '' });
  const [flashMessage, setFlashMessage] = useState('');

  useEffect(() => {
    const fetchStoreData = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/store`);
        if (response.status === 200) {
          setStoreExists(true);
          setStoreName(response.data.name);
          setStoreDescription(response.data.description);
          setStoreLocation(response.data.location);
          setItems(response.data.items || []);
        }
      } catch (error) {
        console.error('Error fetching store data:', error);
      }
    };

    fetchStoreData();
  }, [authUser._id]);

  const handleStoreNameChange = (e) => {
    setStoreName(e.target.value);
  };

  const handleStoreDescriptionChange = (e) => {
    setStoreDescription(e.target.value);
  };

  const handleStoreLocationChange = (e) => {
    setStoreLocation(e.target.value);
  };

  const handleNewItemChange = (e) => {
    const { name, value } = e.target;
    setNewItemData({ ...newItemData, [name]: value });
  };

  const addItem = async () => {
    if (newItemData.title === '' || newItemData.description === '' || newItemData.price === '' || newItemData.quantity === '') {
      setFlashMessage('Please fill in all fields before adding a new item.');
      return;
    }

    try {
      const response = await axios.post(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/store/items`, newItemData);

      if (response.status === 200) {
        console.log('Item added successfully');
        const newItem = response.data; // Use the response data which includes the generated _id
        setItems([...items, newItem]);
        setNewItemData({ title: '', description: '', price: '', quantity: '' });
        setFlashMessage('');
      }
    } catch (error) {
      console.error('Error adding new item:', error);
    }
  };

  const removeItem = async (itemId, index) => {
    if (!itemId) {
      console.error('Invalid item ID');
      return;
    }

    try {
      const response = await axios.delete(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/store/items/${itemId}`);

      if (response.status === 200) {
        console.log('Item removed successfully');
        const updatedItems = items.filter((item, i) => i !== index);
        setItems(updatedItems);
      }
    } catch (error) {
      console.error('Error removing item:', error);
    }
  };

  const deleteStore = async () => {
    try {
      const response = await axios.delete(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/store`);
      if (response.status === 200) {
        console.log('Store deleted successfully');
        setStoreExists(false);
        setStoreName('');
        setStoreDescription('');
        setStoreLocation('');
        setItems([]);
      }
    } catch (error) {
      console.error('Error deleting store:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const userId = authUser._id;
    const baseURL = 'http://localhost:5000/api/users/';
    const requestURL = `${baseURL}${encodeURIComponent(userId)}/store`;

    if (!storeExists) {
      const store = { name: storeName, description: storeDescription, location: storeLocation, items: items };

      try {
        const response = await axios.post(requestURL, store);
        if (response.status === 201) {
          console.log('Store added successfully:', response.data);
          setStoreExists(true);
        }
      } catch (error) {
        console.error('Error adding store:', error);
      }
    } else {
      addItem();
    }
  };

  return (
    <div>
      {storeExists ? (
        <>
          <h1>{storeName}</h1>
          <p>{storeDescription}</p> {/* Show store description */}
          <p>{storeLocation}</p> {/* Show store location */}
          <div>
            <button type="button" onClick={deleteStore} style={{ marginLeft: '20px' }}>Delete Store</button>
          </div>
        </>
      ) : (
        <h1>Create Store</h1>
      )}
      <form onSubmit={handleSubmit}>
        {!storeExists && (
          <>
            <div>
              <label>Store Name:</label>
              <input type="text" value={storeName} onChange={handleStoreNameChange} required />
            </div>
            <div>
              <label>Store Description:</label>
              <input type="text" value={storeDescription} onChange={handleStoreDescriptionChange} required />
            </div>
            <div>
              <label>Store Location:</label>
              <input type="text" value={storeLocation} onChange={handleStoreLocationChange} required />
            </div>
          </>
        )}
        <h2>Items</h2>
        {items.map((item, index) => (
          <div key={item._id || index} style={{ marginBottom: '10px', padding: '10px', border: '1px solid #ccc', borderRadius: '5px' }}>
            <div>
              <h3>{item.title}</h3>
              <p>{item.description}</p>
              <p>Price: {item.price}</p>
              <p>Quantity: {item.quantity}</p>
              <button type="button" onClick={() => removeItem(item._id, index)} style={{ marginTop: '10px' }}>Remove Item</button>
            </div>
          </div>
        ))}
        <div style={{ marginTop: '20px', padding: '10px', border: '1px solid #ccc', borderRadius: '5px' }}>
          <h2>Add New Item</h2>
          <div>
            <label>Title:</label>
            <input type="text" name="title" value={newItemData.title} onChange={handleNewItemChange} required />
          </div>
          <div>
            <label>Price:</label>
            <input type="number" name="price" value={newItemData.price} onChange={handleNewItemChange} required />
          </div>
          <div>
            <label>Quantity:</label>
            <input type="number" name="quantity" value={newItemData.quantity} onChange={handleNewItemChange} required />
          </div>
          <div>
            <label>Description:</label>
            <input type="text" name="description" value={newItemData.description} onChange={handleNewItemChange} required />
          </div>
          <button type="button" onClick={addItem} style={{ marginTop: '10px' }}>Add New Item</button>
        </div>
        {flashMessage && <div style={{ color: 'red', marginTop: '10px' }}>{flashMessage}</div>}
      </form>
    </div>
  );
};

export default CreateStore;
