import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import UserContext from '../context/UserContext';

const UserO = () => {
  const { authUser } = useContext(UserContext);
  const [orders, setOrders] = useState([]);
  const [expandedOrders, setExpandedOrders] = useState({});

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/orders`);
        if (response.status === 200) {
          setOrders(response.data.orders);
        } else {
          console.error('Error fetching orders: status', response.status);
        }
      } catch (error) {
        console.error('Error fetching orders:', error);
      }
    };

    if (authUser && authUser._id) {
      fetchOrders();
    }
  }, [authUser]);

  const toggleOrder = (index) => {
    setExpandedOrders(prevState => ({
      ...prevState,
      [index]: !prevState[index]
    }));
  };

  return (
    <div>
      <h1>User Orders</h1>
      {orders.length > 0 ? (
        orders.map((order, index) => (
          <div key={order.mainkey} style={{ marginBottom: '10px', padding: '10px', border: '1px solid #ccc', borderRadius: '5px' }}>
            <div onClick={() => toggleOrder(index)} style={{ cursor: 'pointer', borderBottom: '1px solid black', paddingBottom: '10px' }}>
              <h2>Order {order.orderNumber}</h2>
              <p>Status: {order.status}</p>
              <p>Username: {authUser.username}</p>
              <p>Name on Card: {order.ccname}</p>
              <p>Cart Total: ${order.cartTotal ? order.cartTotal.toFixed(2) : 'N/A'}</p>
            </div>
            {expandedOrders[index] && (
              <div>
                <h3>Items:</h3>
                {order.items.map((item, itemIndex) => (
                  <div key={itemIndex} style={{ marginBottom: '5px' }}>
                    <p>Store Name: {item.storeName}</p>
                    <p>Item Name: {item.itemName}</p>
                    <p>Quantity: {item.quantity}</p>
                    <p>Price: ${item.price ? item.price.toFixed(2) : 'N/A'}</p>
                  </div>
                ))}
              </div>
            )}
          </div>
        ))
      ) : (
        <p>No orders found</p>
      )}
    </div>
  );
};

export default UserO;
