import { useContext, useRef, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { api } from '../utils/apiHelper';

import ErrorsDisplay from './ErrorsDisplay';
import UserContext from '../context/UserContext';
import ThemeContext from '../context/ThemeContext';

const UserSignUp = () => {
  const { signIn } = useContext(UserContext);
  const { accentColor } = useContext(ThemeContext);
  const navigate = useNavigate();

  const name = useRef(null);
  const username = useRef(null);
  const email = useRef(null);
  const password = useRef(null);
  const kind = useRef(null);
  const [errors, setErrors] = useState([]);
  const [verificationCode, setVerificationCode] = useState('');
  const [isVerificationVisible, setVerificationVisible] = useState(false);
  const [userEmail, setUserEmail] = useState('');

  const isValidEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const checkUserExists = async (email, username) => {
    const response = await api("/check-user", "POST", { email, username });
    if (response.status === 400) {
      const data = await response.json();
      return data.errors;
    }
    return null;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const user = {
      name: name.current.value.trim(),
      username: username.current.value.trim(),
      email: email.current.value.trim(),
      password: password.current.value.trim(),
      kind: kind.current.value.trim().length > 4 ? "admin" : "user",
    };

    if (!user.name || !user.username || !user.email || !user.password) {
      setErrors(['All fields are required']);
      return;
    }

    if (!isValidEmail(user.email)) {
      setErrors(['Please enter a valid email']);
      return;
    }

    const userExistsErrors = await checkUserExists(user.email, user.username);
    if (userExistsErrors) {
      setErrors(Object.values(userExistsErrors));
      return;
    }

    try {
      const response = await api("/send-verification-code", "POST", user);
      if (response.status === 200) {
        setUserEmail(user.email);
        setVerificationVisible(true);
        // Clear input fields
        name.current.value = '';
        username.current.value = '';
        email.current.value = '';
        password.current.value = '';
        kind.current.value = '';
      } else if (response.status === 400) {
        const data = await response.json();
        setErrors(data.errors);
      } else {
        throw new Error();
      }
    } catch (error) {
      console.log(error);
      navigate("/error");
    }
  };

  const handleVerify = async (event) => {
    event.preventDefault();
    try {
      const response = await api("/verify", "POST", { email: userEmail, code: verificationCode });
      if (response.status === 200) {
        console.log("Email verified successfully");
        const userData = {
          email: userEmail,
          password: password.current.value, // Use the password entered during sign-up
        };
        const signInSuccess = await signIn(userData);
        if (signInSuccess) {
          navigate("/authenticated");
        } else {
          setErrors(['Sign-in failed. Please try again.']);
        }
      } else {
        const data = await response.json();
        setErrors([data.message]);
      }
    } catch (error) {
      console.log(error);
      navigate("/error");
    }
  };

  const handleCancel = (event) => {
    event.preventDefault();
    navigate("/");
  };

  return (
    <div className="bounds">
      <div className="grid-33 centered signin">
        <h1>Sign up</h1>
        <div>
          <ErrorsDisplay errors={errors} />
          <form onSubmit={handleSubmit}>
            <input id="name" name="name" type="text" ref={name} placeholder="Name" />
            <input id="username" name="username" type="text" ref={username} placeholder="User Name" />
            <input id="email" name="email" type="email" ref={email} placeholder="Email" />
            <input id="password" name="password" type="password" ref={password} placeholder="Password" />
            <input id="kind" name="kind" type="text" ref={kind} placeholder="Kind" />
            <div className="pad-bottom">
              <button className="button" type="submit" style={{ background: accentColor }}>Sign up</button>
              <button className="button button-secondary" style={{ color: accentColor }} onClick={handleCancel}>Cancel</button>
            </div>
          </form>
          {isVerificationVisible && (
            <form onSubmit={handleVerify}>
              <input
                id="verificationCode"
                name="verificationCode"
                type="text"
                value={verificationCode}
                onChange={(e) => setVerificationCode(e.target.value)}
                placeholder="Enter Verification Code"
              />
              <button className="button" type="submit" style={{ background: accentColor }}>Verify</button>
            </form>
          )}
        </div>
        <p>
          Already have a user account? <Link style={{ color: accentColor }} to="/signin">Click here</Link> to sign in!
        </p>
      </div>
    </div>
  );
};

export default UserSignUp;
