import { useContext, useRef, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { api } from '../utils/apiHelper';

import ErrorsDisplay from './ErrorsDisplay';
import UserContext from '../context/UserContext';
import ThemeContext from '../context/ThemeContext';

const UserSignIn = () => {
  const { actions } = useContext(UserContext);
  const { accentColor } = useContext(ThemeContext);
  const navigate = useNavigate();

  // State
  const name = useRef(null);
  const username = useRef(null);
  const email = useRef(null); // Add email reference
  const password = useRef(null);
  const kind = useRef(null);
  const [errors, setErrors] = useState([]);

  // event handlers
  const handleSubmit = async (event) => {
    event.preventDefault();

    const user = {
      name: name.current.value,
      username: username.current.value,
      email: email.current.value, // Add email to user object
      password: password.current.value,
      kind: kind.current.value.length > 4 ? "admin" : "user",
    };

    // Client-side validation
    if (!user.name || !user.username || !user.email || !user.password) {
      setErrors(['All fields are required']);
      return;
    }

    try {
      const response = await api("/users", "POST", user);
      if (response.status === 201) {
        console.log(`${user.username} is successfully signed up and authenticated`);
        await actions.signIn(user);
        navigate("/authenticated");
      } else if (response.status === 400) {
        const data = await response.json();
        setErrors(data.errors);
      } else {
        throw new Error();
      }
    } catch (error) {
      console.log(error);
      navigate("/error");
    }
  };

  const handleCancel = (event) => {
    event.preventDefault();
    navigate("/");
  };

  return (
    <div className="bounds">
      <div className="grid-33 centered signin">
        <h1>Sign up</h1>
        <div>
          <ErrorsDisplay errors={errors} />
          <form onSubmit={handleSubmit}>
            <input
              id="name"
              name="name"
              type="text"
              ref={name}
              placeholder="Name"
            />
            <input
              id="username"
              name="username"
              type="text"
              ref={username}
              placeholder="User Name"
            />
            <input
              id="email"
              name="email"
              type="email"
              ref={email}
              placeholder="Email" // Add email input field
            />
            <input
              id="password"
              name="password"
              type="password"
              ref={password}
              placeholder="Password"
            />
            <input
              id="kind"
              name="kind"
              type="text"
              ref={kind}
              placeholder="Kind"
            />
            <div className="pad-bottom">
              <button className="button" type="submit" style={{ background: accentColor }}>Sign up</button>
              <button className="button button-secondary" style={{ color: accentColor }} onClick={handleCancel}>Cancel</button>
            </div>
          </form>
        </div>
        <p>
          Already have a user account? <Link style={{ color: accentColor }} to="/signin">Click here</Link> to sign in!
        </p>
      </div>
    </div>
  );
};

export default UserSignIn;
