import React, { useState, useEffect, useRef } from 'react';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css'; // requires a loader
import '../styles/Home.css'; // Ensure this path is correct

const Home = () => {
  const [videoVisible, setVideoVisible] = useState(true);
  const [selectedItem, setSelectedItem] = useState(0);
  const carouselRef = useRef(null);
  const videoRef = useRef(null);

  useEffect(() => {
    const timer = setTimeout(() => {
      setVideoVisible(false);
      setSelectedItem(1); // Move to the next slide after 25 seconds
    }, 25000); // 25 seconds

    return () => clearTimeout(timer); // Clear timeout if component unmounts
  }, []);

  useEffect(() => {
    if (carouselRef.current) {
      carouselRef.current.moveTo(selectedItem);
    }
    if (selectedItem === 0 && videoRef.current) {
      videoRef.current.load(); // Reload the video when it becomes visible
      setVideoVisible(true); // Reset the visibility
    }
  }, [selectedItem]);

  return (
    <div className="container">
      <h1 className="heading">Welcome</h1>
      <div className="carouselContainer">
        <Carousel
          ref={carouselRef}
          selectedItem={selectedItem}
          showThumbs={false}
          autoPlay={false}
          infiniteLoop={true}
          interval={25000}
          onChange={(index) => setSelectedItem(index)}
        >
          <div>
            <video
              ref={videoRef}
              className="video"
              style={{ opacity: videoVisible ? 1 : 0 }}
              autoPlay
              muted
              controls
            >
              <source src="/ftphomescreen.mp4" type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          </div>
          <div>
            <img src="/tfp.png" alt="Example" className="image" />
          </div>
        </Carousel>
      </div>
    </div>
  );
};

export default Home;
