import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import UserContext from '../context/UserContext';

const StripePay = ({ total }) => {
  const { authUser } = useContext(UserContext);
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();
  const [error, setError] = useState(null);
  const [succeeded, setSucceeded] = useState(false);
  const [processing, setProcessing] = useState(false);
  const [clientSecret, setClientSecret] = useState('');
  const [name, setName] = useState('');
  const [cart, setCart] = useState([]);

  useEffect(() => {
    const fetchCartData = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/cart`);
        if (response.status === 200) {
          setCart(response.data.cart);
        } else {
          console.error('Error fetching cart data: status', response.status);
        }
      } catch (error) {
        console.error('Error fetching cart data:', error);
      }
    };

    const createPaymentIntent = async () => {
      try {
        const response = await axios.post('http://localhost:5000/api/create-payment-intent', { amount: total });
        setClientSecret(response.data.clientSecret);
      } catch (error) {
        console.error('Error creating payment intent:', error);
      }
    };

    if (total > 0) {
      fetchCartData();
      createPaymentIntent();
    }
  }, [total, authUser]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setProcessing(true);

    const payload = await stripe.confirmCardPayment(clientSecret, {
      payment_method: {
        card: elements.getElement(CardElement),
        billing_details: {
          name: name,
        },
      },
    });

    if (payload.error) {
      setError(`Payment failed: ${payload.error.message}`);
      setProcessing(false);
    } else {
      setError(null);
      setSucceeded(true);
      setProcessing(false);

      /*// Update store items' quantities
      await Promise.all(cart.map(async (cartItem) => {
        await axios.put(`http://localhost:5000/api/stores/update-quantity`, {
          quantity: cartItem.quantity,
          StrID: cartItem.StrID,
          itemName: cartItem.itemName
        });
      }));*/

      /*// Clear user's cart
      await axios.delete(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/cart/clear`);

      // Navigate to the homepage
      navigate('/');*/

      // Clear payment fields
      setName('');
      elements.getElement(CardElement).clear();
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>
          Name
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </label>
      </div>
      <CardElement />
      <button disabled={processing || succeeded}>
        {processing ? 'Processing...' : 'Pay Now'}
      </button>
      {error && <div>{error}</div>}
      {succeeded && <div>Payment succeeded!</div>}
    </form>
  );
};

export default StripePay;
