import useTheme from './useTheme';

export const useDefaultStyles = () => {
  const { accentColor } = useTheme();

  return {
    border: `2px solid ${accentColor}`,
    borderRadius: '5px',
    padding: '10px',
    margin: '10px 0',
    fontFamily: 'Arial, sans-serif',
    color: '#333'
  };
};

export const paragraphStyles = {
  margin: '10px 0',
  lineHeight: '1.5',
};

export const divStyles = {
  margin: '10px 0',
  padding: '20px 0'
};

export const buttonStyles = {
  border: 'none',
  padding: '10px 15px',
  borderRadius: '3px',
  cursor: 'pointer',
};

export const buttonReadyStyles = {
  ...buttonStyles,
  backgroundColor: 'grey',
  color: 'white',
};

export const buttonDeleteStyles = {
  ...buttonStyles,
  backgroundColor: 'red',
  color: 'white',
  position: 'absolute',
  top: '10px',
  right: '10px',
};

export const marginStyles = {
  small: {
    margin: '5px',
  },
  medium: {
    margin: '10px',
  },
  large: {
    margin: '15px',
  },
};

export const useStoresListStyles = () => {
  const { accentColor } = useTheme();

  return {
    container: {
      margin: '20px auto',
      padding: '20px',
      maxWidth: '1200px',
      borderRadius: '8px',
      border: `1px solid ${accentColor}`,
      backgroundColor: '#f9f9f9'
    },
    storeContainer: {
      border: `2px solid ${accentColor}`,
      borderRadius: '5px',
      padding: '15px',
      marginBottom: '20px',
      backgroundColor: '#fff'
    },
    storeTitle: {
      color: '#333',
      cursor: 'pointer',
      marginBottom: '10px'
    },
    storeDetails: {
      fontSize: '0.8em',
      color: '#666'
    }
  };
};
