import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { defaultStyles, paragraphStyles, divStyles } from '../context/Default_Designs';

const StoresList = () => {
  const [stores, setStores] = useState([]);

  useEffect(() => {
    const fetchStores = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/stores');
        if (response.status === 200) {
          setStores(response.data.stores);
        } else {
          console.error('Error fetching stores: status', response.status);
        }
      } catch (error) {
        console.error('Error fetching stores:', error);
      }
    };

    fetchStores();
  }, []);

  return (
    <div style={divStyles}>
      <h1>All Stores</h1>
      {stores.length > 0 ? (
        stores.map((store, index) => (
          <div key={store.userId} style={{ ...defaultStyles, border: `2px solid #000`, marginBottom: '20px' }}>
            <h2 style={{ color: '#333' }}>{store.name}</h2>
            <p style={paragraphStyles}>Description: {store.description}</p>
            <p style={paragraphStyles}>Location: {store.location}</p>
          </div>
        ))
      ) : (
        <p>No stores found</p>
      )}
    </div>
  );
};

export default StoresList;
