import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import UserContext from '../context/UserContext';

const stripePromise = loadStripe('pk_live_nZvWGLv87Odj065JZPm631Ce');

const PaymentProcess = () => {
  const { authUser } = useContext(UserContext);
  const navigate = useNavigate();
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);
  const [editIndex, setEditIndex] = useState(null);
  const [quantity, setQuantity] = useState('');
  const [notes, setNotes] = useState('');

  useEffect(() => {
    if (!authUser || !authUser._id) {
      console.error('User ID is undefined');
      return;
    }

    const fetchCartData = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/cart`);
        if (response.status === 200) {
          setCart(response.data.cart);
          const totalAmount = response.data.cart.reduce((acc, item) => acc + (item.price * item.quantity), 0);
          setTotal(totalAmount);
        } else {
          console.error('Error fetching cart data: status', response.status);
        }
      } catch (error) {
        console.error('Error fetching cart data:', error);
      }
    };

    fetchCartData();

  }, [authUser]);

  const handleDeleteItem = async (index) => {
    try {
      const itemId = cart[index]._id; // Assuming _id is the unique identifier of the item
      const response = await axios.delete(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/cart/items/${encodeURIComponent(itemId)}`);
      if (response.status === 200) {
        const updatedCart = [...cart];
        updatedCart.splice(index, 1); // Remove the item at the specified index
        setCart(updatedCart);
        const totalAmount = updatedCart.reduce((acc, item) => acc + (item.price * item.quantity), 0);
        setTotal(totalAmount);
      } else {
        console.error('Error deleting cart item:', response.status);
      }
    } catch (error) {
      console.error('Error deleting cart item:', error);
    }
      

  };
  
  const handleEditItem = (index) => {
    const cartItem = cart[index];
    setEditIndex(index);
    setQuantity(cartItem.quantity);
    setNotes(cartItem.notes);
  };

  const handleCancelEdit = () => {
    setEditIndex(null);
    setQuantity('');
    setNotes('');
  };


  const handleGoBack = () => {
    navigate('/');
  };

  return (
    <div>
      <h1>My Cart</h1>
      {cart.length > 0 ? (
        cart.map((cartItem, index) => (
          <div key={index} style={{ marginBottom: '10px', padding: '10px', border: '1px solid #ccc', borderRadius: '5px' }}>
            {editIndex === index ? (
              <div>
                <p>Title: {cartItem.storeName}</p> {/* Display store name */}
                <p>Price: ${parseFloat(cartItem.price).toFixed(2)}</p>
                <p>Quantity: <input type="number" min="1" value={quantity} onChange={(e) => setQuantity(e.target.value)} /></p>
                <p>Notes: <input type="text" value={notes} onChange={(e) => setNotes(e.target.value)} /></p>
               
                <button onClick={handleCancelEdit}>Cancel</button>
              </div>
            ) : (
              <div>
                <p>{cartItem.itemName}</p>
                <p>Price: ${parseFloat(cartItem.price).toFixed(2)}</p>
                <p>Quantity: {cartItem.quantity}</p>
                <p>Notes: {cartItem.notes}</p>
                <button onClick={() => handleEditItem(index)}>Edit</button>
                <button onClick={() => handleDeleteItem(index)}>Delete</button>
               
              </div>
            )}
          </div>
        ))
      ) : (
        <p>Cart is empty</p>
      )}
      <h3>Total: ${total.toFixed(2)}</h3>
      <button onClick={handleGoBack}>Go Back</button>
    </div>
  );
};



const CheckoutForm = ({ total }) => {
  const stripe = useStripe();
  const elements = useElements();
  const [error, setError] = useState(null);
  const [succeeded, setSucceeded] = useState(false);
  const [processing, setProcessing] = useState(false);
  const [clientSecret, setClientSecret] = useState('');

  useEffect(() => {
    const createPaymentIntent = async () => {
      try {
        const response = await axios.post('http://localhost:5000/api/create-payment-intent', { amount: total });
        setClientSecret(response.data.clientSecret);
      } catch (error) {
        console.error('Error creating payment intent:', error);
      }
    };

    createPaymentIntent();
  }, [total]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setProcessing(true);

    const payload = await stripe.confirmCardPayment(clientSecret, {
      payment_method: {
        card: elements.getElement(CardElement),
      },
    });

    if (payload.error) {
      setError(`Payment failed: ${payload.error.message}`);
      setProcessing(false);
    } else {
      setError(null);
      setSucceeded(true);
      setProcessing(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <CardElement />
      <button disabled={processing || succeeded}>
        {processing ? 'Processing...' : 'Pay Now'}
      </button>
      {error && <div>{error}</div>}
      {succeeded && <div>Payment succeeded!</div>}
    </form>
  );
};


export default PaymentProcess;
