import React, { useState, useRef } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import ErrorsDisplay from './ErrorsDisplay';
import UserContext from '../context/UserContext';
import ThemeContext from '../context/ThemeContext';

const UserSignUp = () => {
  const { actions } = React.useContext(UserContext);
  const { accentColor } = React.useContext(ThemeContext);
  const navigate = useNavigate();

  const name = useRef(null);
  const username = useRef(null);
  const email = useRef(null);
  const password = useRef(null);
  const passwordA = useRef(null);
  const kind = useRef(null);
  const [errors, setErrors] = useState([]);
  const [verificationCode, setVerificationCode] = useState('');
  const [isVerificationVisible, setVerificationVisible] = useState(false);
  const [generatedCode, setGeneratedCode] = useState('');
  const [userEmail, setUserEmail] = useState('');

  const isValidEmail = (email) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);

  const checkUserExists = async (email, username) => {
    const response = await api("/check-user", "POST", { email, username });
    if (response.status === 400) {
      const data = await response.json();
      return data.errors;
    }
    return null;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const user = {
      name: name.current.value,
      username: username.current.value,
      email: email.current.value,
      password: password.current.value,
      passwordA: passwordA.current.value,
      kind: kind.current.value.length > 4 ? "admin" : "user",
    };

    // Client-side validation
    let validationErrors = [];
    if (!user.name || !user.username || !user.email || !user.password || !user.passwordA || !user.kind) {
      validationErrors.push('All fields are required.');
    }
    if (user.password !== user.passwordA) {
      validationErrors.push('Passwords do not match.');
    }
    if (!isValidEmail(user.email)) {
      validationErrors.push('Invalid email format.');
    }

    setErrors(validationErrors);

    if (validationErrors.length === 0) {
      const userExistsErrors = await checkUserExists(user.email, user.username);
      if (userExistsErrors) {
        setErrors(Object.values(userExistsErrors));
        return;
      }

      const code = Math.floor(100000 + Math.random() * 900000).toString();
      setGeneratedCode(code);

      try {
        await axios.post('/verify', { name: user.name, email: user.email, verifyGuy: code });
        setUserEmail(user.email);
        setVerificationVisible(true);
      } catch (error) {
        console.error('Error sending verification email:', error);
      }
    }
  };

  const handleVerification = async (event) => {
    event.preventDefault();

    if (verificationCode !== generatedCode) {
      setErrors(['Invalid verification code.']);
      return;
    }

    try {
      const user = {
        name: name.current.value,
        username: username.current.value,
        email: userEmail,
        password: password.current.value,
        kind: kind.current.value.length > 4 ? "admin" : "user",
      };
      const response = await axios.post('/users/add', user);
      console.log(response.data);
      await actions.signIn(user);
      navigate("/authenticated");
    } catch (error) {
      console.error('Error creating user:', error);
      setErrors(['Error creating user. Please try again.']);
    }
  };

  const handleCancel = (event) => {
    event.preventDefault();
    navigate("/");
  };

  return (
    <div className="bounds">
      <div className="grid-33 centered signin">
        <h1>Sign up</h1>
        <div>
          <ErrorsDisplay errors={errors} />
          {!isVerificationVisible ? (
            <form onSubmit={handleSubmit}>
              <input id="name" name="name" type="text" ref={name} placeholder="Name" />
              <input id="username" name="username" type="text" ref={username} placeholder="User Name" />
              <input id="email" name="email" type="email" ref={email} placeholder="Email" />
              <input id="password" name="password" type="password" ref={password} placeholder="Password" />
              <input id="passwordA" name="passwordA" type="password" ref={passwordA} placeholder="Re-type Password" />
              <input id="kind" name="kind" type="text" ref={kind} placeholder="Kind" />
              <div className="pad-bottom">
                <button className="button" type="submit" style={{ background: accentColor }}>Register</button>
                <button className="button button-secondary" style={{ color: accentColor }} onClick={handleCancel}>Cancel</button>
              </div>
            </form>
          ) : (
            <form onSubmit={handleVerification}>
              <input
                id="verificationCode"
                name="verificationCode"
                type="text"
                value={verificationCode}
                onChange={(e) => setVerificationCode(e.target.value)}
                placeholder="Enter Verification Code"
              />
              <button className="button" type="submit" style={{ background: accentColor }}>Verify</button>
            </form>
          )}
        </div>
        <p>
          Already have a user account? <Link style={{ color: accentColor }} to="/signin">Click here</Link> to sign in!
        </p>
      </div>
    </div>
  );
};

export default UserSignUp;
