import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import UserContext from '../context/UserContext';

const UpdateStoreStore = () => {
   
    const [storeName, setStoreName] = useState('');
    const [storeDescription, setStoreDescription] = useState('');
    const [storeLocation, setStoreLocation] = useState('');
    const [items, setItems] = useState([{ title: '', price: '', quantity: '', description: '', highlightColorIndex: 0 }]);
    const [storeExists, setStoreExists] = useState(false);
    const { authUser } = useContext(UserContext);
    const [highlightColorIndex, setHighlightColorIndex] = useState(1);
    const [editItemIndex, setEditItemIndex] = useState(null);
    const [editItemData, setEditItemData] = useState({ title: '', description: '', price: '', quantity: '' });
  
    const highlightColors = ['white', 'lightblue', 'lightgreen'];
  
    useEffect(() => {
      const fetchStoreData = async () => {
        try {
          const response = await axios.get(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/store`);
          if (response.status === 200) {
            setStoreExists(true);
            setStoreName(response.data.name);
            setStoreDescription(response.data.description);
            setStoreLocation(response.data.location);
            setItems(response.data.items || []);
          }
        } catch (error) {
          console.error('Error fetching store data:', error);
        }
      };
  
      fetchStoreData();
    }, [authUser._id]);
    const deleteStore = async () => {
      try {
        const response = await axios.delete(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/store`);
        if (response.status === 200) {
          console.log('Store deleted successfully');
          setStoreExists(false);
          setStoreName('');
          setStoreDescription('');
          setStoreLocation('');
          setItems([{ title: '', price: '', quantity: '', description: '', highlightColorIndex: 0 }]);
        }
      } catch (error) {
        console.error('Error deleting store:', error);
      }
    };
  
    const startEditItem = (index) => {
      setEditItemIndex(index);
      setEditItemData(items[index]);
    };
  
    const handleEditItemChange = (e) => {
      const { name, value } = e.target;
      setEditItemData({ ...editItemData, [name]: value });
    };
  
    const saveEditItem = async () => {
      try {
        const response = await axios.put(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/store/items/${editItemIndex}`, editItemData);
        if (response.status === 200) {
          console.log('Item updated successfully');
          const updatedItems = items.map((item, index) => index === editItemIndex ? editItemData : item);
          setItems(updatedItems);
          setEditItemIndex(null);
        }
      } catch (error) {
        console.error('Error updating item:', error);
      }
    };
  
    const handleSubmit = async (e) => {
        e.preventDefault();
        const userId = authUser._id;
        const baseURL = 'http://localhost:5000/api/users/';
        const requestURL = `${baseURL}${encodeURIComponent(userId)}/store`;
    
        if (!storeExists) {
          const store = { name: storeName, description: storeDescription, location: storeLocation, items: items };
    
          try {
            const response = await axios.post(requestURL, store);
            if (response.status === 201) {
              console.log('Store added successfully:', response.data);
              setStoreExists(true);
              clearItemFields();
            }
          } catch (error) {
            console.error('Error adding store:', error);
          }
        } else {
          const newItem = items[items.length - 1];
    
          try {
            const response = await axios.post(`${requestURL}/items`, newItem);
            if (response.status === 200) {
              console.log('Item added successfully:', response.data);
              clearItemFields();
            }
          } catch (error) {
            console.error('Error adding item:', error);
          }
        }
      };
    
      const clearItemFields = () => {
        setItems([{ title: '', price: '', quantity: '', description: '', highlightColorIndex: 0 }]);
      };
    
    return (
        <div>
        {storeExists ? (
          <>
            <h1>{storeName}</h1>
            <p>{storeDescription}</p>
            <p>{storeLocation}</p>
            <button onClick={deleteStore}>Delete Store</button>
          </>
        ) : (
          <h1>Create Store</h1>
        )}
        <form onSubmit={handleSubmit}>
          {!storeExists && (
            <>
              <div>
                <label>Store Name:</label>
                <input type="text" value={storeName} onChange={handleStoreNameChange} required />
              </div>
              <div>
                <label>Store Description:</label>
                <input type="text" value={storeDescription} onChange={handleStoreDescriptionChange} required />
              </div>
              <div>
                <label>Store Location:</label>
                <input type="text" value={storeLocation} onChange={handleStoreLocationChange} required />
              </div>
            </>
          )}
          <h2>Items</h2>
          {items.map((item, index) => (
            <div key={index} style={{ backgroundColor: highlightColors[item.highlightColorIndex || 0], marginBottom: '10px', padding: '10px', border: '1px solid #ccc', borderRadius: '5px' }}>
              <div>
                <label>Title:</label>
                <input type="text" name="title" value={item.title} onChange={(e) => handleItemChange(index, e)} required />
              </div>
              <div>
                <label>Price:</label>
                <input type="number" name="price" value={item.price} onChange={(e) => handleItemChange(index, e)} required />
              </div>
              <div>
                <label>Quantity:</label>
                <input type="number" name="quantity" value={item.quantity} onChange={(e) => handleItemChange(index, e)} required />
              </div>
              <div>
                <label>Description:</label>
                <input type="text" name="description" value={item.description} onChange={(e) => handleItemChange(index, e)} required />
              </div>
              {items.length > 1 && (
                <button type="button" onClick={() => removeItem(index)} style={{ marginTop: '10px' }}>Remove Field</button>
              )}
            </div>
          ))}
          <button type="button" onClick={addItem} style={{ marginRight: '10px' }}>New Field</button>
          <button type="submit" style={{ marginRight: '10px' }}>{storeExists ? 'Add Item' : 'Create Store'}</button>
        </form>
        {storeExists && (
          <StoreItems items={items} onDeleteItem={removeItem} onEditItem={startEditItem} />
        )}
        {editItemIndex !== null && (
          <div>
            <h2>Edit Item</h2>
            <div>
              <label>Title:</label>
              <input type="text" name="title" value={editItemData.title} onChange={handleEditItemChange} required />
            </div>
            <div>
              <label>Price:</label>
              <input type="number" name="price" value={editItemData.price} onChange={handleEditItemChange} required />
            </div>
            <div>
              <label>Quantity:</label>
              <input type="number" name="quantity" value={editItemData.quantity} onChange={handleEditItemChange} required />
            </div>
            <div>
              <label>Description:</label>
              <input type="text" name="description" value={editItemData.description} onChange={handleEditItemChange} required />
            </div>
            <button onClick={saveEditItem} style={{ marginRight: '10px' }}>Save</button>
            <button onClick={() => setEditItemIndex(null)}>Cancel</button>
          </div>
        )}
      </div>
    );
  };
  
  export default UpdateStore;
