import { createContext, useState } from "react";
//import Cookies from "js-cookie";
import { hashPassword, comparePassword} from '../helpers/auth';
import jwt from 'jsonwebtoken';
import { api } from "../utils/apiHelper";

const UserContext = createContext(null);

export const UserProvider = (props) => {
  const cookie = Cookies.get("authenticatedUser");
  const [authUser, setAuthUser] = useState(cookie ? JSON.parse(cookie) : null);

  const signIn = async (req, res, credentials) => {


    const response = await api("/users", "GET", null, credentials);
    if (response.status === 200){
     const user = await response.json();
     setAuthUser(user);
     const match = await comparePassword(credentials.password, user.password)
     if(match) {
      jwt.sign({email: user.email, id: user._id, name: user.name, kind: user.kind}, process.env.TOKEN_SECRET, {}, (err, token) => {
          if(err) throw err;
          res.cookie('authenticatedUser', token).json(user)

          })
      }
          if(!match) {
            res.json({
                error: "Passwords do not match"
            })
        }
    return user
       } else if (response.status === 401) {
     return null
   } else {
     throw new Error();
   } 

  }

  const getProfile = (req, res) => {
    const {token} = req.cookies
    if(token) {
        jwt.verify(token, process.env.TOKEN_SECRET, {}, (err, user) => {
            if(err) throw err;
            res.json(user)
        })
    } else {
      res.json(null)  
    }
    }


  const signOut = (req, res) => {
      setAuthUser(null);
      res.clearCookie('token');
      return res.sendStatus(200);
  }

  return (
    <UserContext.Provider value={{
      authUser,
      actions: {
        signIn,
        signOut,
        getProfile
      }
    }}>
      {props.children}
    </UserContext.Provider>
  );
}

export default UserContext;
