import React, { useState, useContext, useEffect } from 'react';
import axios from 'axios';
import UserContext from '../context/UserContext';
import '../styles/compliment.css';

const Compliment = () => {
  const { authUser } = useContext(UserContext);
  const [title, setTitle] = useState('');
  const [amount, setAmount] = useState('');
  const [startDate, setStartDate] = useState('');
  const [startTime, setStartTime] = useState('');
  const [endTime, setEndTime] = useState('');
  const [quantity, setQuantity] = useState('');
  const [createdCompliments, setCreatedCompliments] = useState([]);
  const [followers, setFollowers] = useState([]);
  const [selectedCompliments, setSelectedCompliments] = useState([]);
  const [selectedFollowers, setSelectedFollowers] = useState([]);
  const [showAll, setShowAll] = useState(false);

  useEffect(() => {
    const fetchCompliments = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/compliments`);
        setCreatedCompliments(response.data.compliments);
      } catch (error) {
        console.error('Error fetching compliments:', error);
      }
    };

    const fetchFollowers = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/users/all`);
        setFollowers(response.data.users);
      } catch (error) {
        console.error('Error fetching followers:', error);
      }
    };

    fetchCompliments();
    fetchFollowers();
  }, [authUser._id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/create-compliment`, {
        title,
        amount,
        startDate,
        startTime,
        endTime,
        quantity,
      });
      setCreatedCompliments(response.data.compliments);
      // Clear input fields
      setTitle('');
      setAmount('');
      setStartDate('');
      setStartTime('');
      setEndTime('');
      setQuantity('');
    } catch (error) {
      console.error('Error creating compliments:', error);
    }
  };

  const handleComplimentSelection = (compliment) => {
    setSelectedCompliments((prevSelected) =>
      prevSelected.includes(compliment)
        ? prevSelected.filter((c) => c !== compliment)
        : [...prevSelected, compliment]
    );
  };

  const handleFollowerSelection = (follower) => {
    setSelectedFollowers((prevSelected) =>
      prevSelected.includes(follower)
        ? prevSelected.filter((f) => f !== follower)
        : [...prevSelected, follower]
    );
  };

  const handleSendCompliments = async () => {
    try {
      const response = await axios.post(`http://localhost:5000/api/users/${encodeURIComponent(authUser._id)}/send-compliments`, {
        compliments: selectedCompliments,
        followers: selectedFollowers,
      });
      console.log('Compliments sent successfully:', response.data);
      // Update the compliments state to mark sent compliments
      const updatedCompliments = createdCompliments.map(compliment =>
        selectedCompliments.includes(compliment)
          ? { ...compliment, sent: true, recipient: selectedFollowers.map(f => f.email).join(', ') }
          : compliment
      );
      setCreatedCompliments(updatedCompliments);
      setSelectedCompliments([]);
      setSelectedFollowers([]);
      alert('Compliments sent successfully');
    } catch (error) {
      console.error('Error sending compliments:', error);
      alert('An error occurred while sending compliments');
    }
  };

  const toggleShowAll = () => {
    setShowAll(!showAll);
  };

  return (
    <div className="compliment-container">
      <div className="compliment-card">
        <h2>Create Compliment</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <input
            type="number"
            placeholder="Amount (%)"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
          <input
            type="date"
            placeholder="Start Date"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
          />
          <input
            type="time"
            placeholder="Start Time"
            value={startTime}
            onChange={(e) => setStartTime(e.target.value)}
          />
          <input
            type="time"
            placeholder="End Time"
            value={endTime}
            onChange={(e) => setEndTime(e.target.value)}
          />
          <input
            type="number"
            placeholder="Quantity"
            value={quantity}
            onChange={(e) => setQuantity(e.target.value)}
          />
          <button type="submit">Create Compliment</button>
        </form>
        {createdCompliments.length > 0 && (
          <div className="compliment-result">
            <h1>Compliments Created</h1>
            {createdCompliments.slice(0, 3).map((compliment, index) => (
              <div key={index} className="compliment-item">
                {!compliment.sent && (
                  <input
                    type="checkbox"
                    checked={selectedCompliments.includes(compliment)}
                    onChange={() => handleComplimentSelection(compliment)}
                  />
                )}
                <p><strong>{index + 1}.</strong> Title: {compliment.title}</p>
                <p>Amount: {compliment.amount}%</p>
                {compliment.sent && (
                  <>
                    <p>Recipient: {compliment.recipient}</p>
                    <p>Recipient's Name: {followers.find(f => f.email === compliment.recipient)?.username}</p>
                  </>
                )}
                <hr />
              </div>
            ))}
            {showAll && createdCompliments.slice(3).map((compliment, index) => (
              <div key={index + 3} className="compliment-item">
                {!compliment.sent && (
                  <input
                    type="checkbox"
                    checked={selectedCompliments.includes(compliment)}
                    onChange={() => handleComplimentSelection(compliment)}
                  />
                )}
                <p><strong>{index + 4}.</strong> Title: {compliment.title}</p>
                <p>Amount: {compliment.amount}%</p>
                {compliment.sent && (
                  <>
                    <p>Recipient: {compliment.recipient}</p>
                    <p>Recipient's Name: {followers.find(f => f.email === compliment.recipient)?.username}</p>
                  </>
                )}
                <hr />
              </div>
            ))}
            {createdCompliments.length > 3 && (
              <button onClick={toggleShowAll} className="toggle-button">
                {showAll ? 'Show Less' : 'Show All'}
              </button>
            )}
          </div>
        )}
        {followers.length > 0 && (
          <div className="followers-list">
            <h1>Select Followers</h1>
            {followers.map((follower, index) => (
              <div key={index} className="follower-item">
                <input
                  type="checkbox"
                  checked={selectedFollowers.includes(follower)}
                  onChange={() => handleFollowerSelection(follower)}
                />
                <p>{follower.username} ({follower.email})</p>
              </div>
            ))}
          </div>
        )}
        {selectedCompliments.length > 0 && selectedFollowers.length > 0 && (
          <button onClick={handleSendCompliments} className="send-button">
            Send Selected Compliments to Selected Followers
          </button>
        )}
      </div>
    </div>
  );
};

export default Compliment;
